options.css:
:root{
      --bg:#0a0b0d;
      --panel:#0f1116cc;
      --panel2:#0c0e12cc;
      --stroke:#1e232d;
      --stroke2:#2a3240;
      --text:#e7ecf7;
      --muted:#94a3b8;
      --ok:#22c55e;
      --warn:#f59e0b;
      --bad:#ef4444;
      --info:#38bdf8;
      --shadow: 0 18px 50px rgba(0,0,0,.45);
      --radius: 18px;
      --radius2: 12px;
      --gutter: 18px;
      --font: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Inter, Arial, "Noto Sans", "Helvetica Neue", sans-serif;
    }

    *{box-sizing:border-box}
    html,body{height:100%}
    body{
      margin:0;
      color:var(--text);
      font-family:var(--font);
      background:
        radial-gradient(1200px 700px at 20% 20%, rgba(34,197,94,.10), transparent 60%),
        radial-gradient(900px 600px at 80% 35%, rgba(56,189,248,.08), transparent 55%),
        radial-gradient(700px 500px at 55% 80%, rgba(245,158,11,.06), transparent 55%),
        linear-gradient(180deg, #07080a, #0a0b0d 40%, #07080a);
      overflow-x:hidden;
    }
    /* subtle triangles pattern */
    body::before{
      content:"";
      position:fixed; inset:0;
      background-image:
        linear-gradient(60deg, rgba(255,255,255,.03) 12%, transparent 12% 88%, rgba(255,255,255,.03) 88%),
        linear-gradient(-60deg, rgba(255,255,255,.02) 12%, transparent 12% 88%, rgba(255,255,255,.02) 88%);
      background-size: 120px 120px;
      opacity:.22;
      pointer-events:none;
      mix-blend-mode:overlay;
    }

    .wrap{
      width:min(1420px, 96vw);
      margin: 20px auto 34px;
      position:relative;
    }

    .panel{
      background: linear-gradient(180deg, rgba(18,21,28,.88), rgba(10,12,16,.82));
      border:1px solid rgba(42,50,64,.7);
      border-top-color: rgba(90,105,130,.28);
      border-left-color: rgba(90,105,130,.22);
      border-radius: var(--radius);
      box-shadow: var(--shadow);
      overflow:hidden;
      position:relative;
    }
    .panel::after{
      content:"";
      position:absolute; inset:0;
      background:
        radial-gradient(900px 200px at 40% -40px, rgba(255,255,255,.10), transparent 60%),
        radial-gradient(600px 240px at 110% 30%, rgba(34,197,94,.06), transparent 55%);
      opacity:.35;
      pointer-events:none;
    }

    .topbar{
      display:flex;
      align-items:center;
      justify-content:space-between;
      padding:18px 22px;
    }
    .brand{
      font-weight:800;
      letter-spacing:.08em;
      font-size: 34px;
      opacity:.95;
    }
    .titleBlock{
      text-align:center;
      line-height:1.05;
      margin: 0 14px;
    }
    .titleBlock .h1{
      font-weight:800;
      letter-spacing:.08em;
      text-transform:uppercase;
      font-size: 20px;
    }
    .titleBlock .sub{
      margin-top:4px;
      font-size:12px;
      color: var(--muted);
      letter-spacing:.03em;
    }
    .clock{
      font-variant-numeric: tabular-nums;
      font-weight:800;
      letter-spacing:.08em;
      font-size: 18px;
      opacity:.95;
    }

    /* main grid */
    .grid{
      display:grid;
      grid-template-columns: 390px 1fr;
      gap: var(--gutter);
      margin-top: var(--gutter);
    }

    .stack{display:grid; gap:var(--gutter)}
    .sectionTitle{
      padding:14px 16px 10px;
      text-transform:uppercase;
      font-weight:800;
      letter-spacing:.12em;
      color:#dbe4ff;
      font-size:14px;
      text-align:center;
      border-bottom:1px solid rgba(42,50,64,.6);
      background: linear-gradient(180deg, rgba(255,255,255,.04), transparent);
      position:relative;
      z-index:1;
    }

    .content{
      padding:14px 16px 16px;
      position:relative;
      z-index:1;
    }

    /* small "status rows" */
    .statusRows{display:grid; gap:10px}
    .statusRow{
      display:flex; align-items:center; justify-content:space-between;
      padding:10px 12px;
      border-radius: 12px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(0,0,0,.10), rgba(0,0,0,.25));
    }
    .left{
      display:flex; align-items:center; gap:10px;
      min-width:0;
    }
    .pillIcon{
      width:26px; height:26px; border-radius:10px;
      border:1px solid rgba(42,50,64,.8);
      background: radial-gradient(circle at 30% 30%, rgba(255,255,255,.12), rgba(0,0,0,.25));
      display:grid; place-items:center;
      color:#cbd5e1;
      flex:0 0 auto;
    }
    .label{
      font-weight:700;
      letter-spacing:.04em;
      color:#d6deee;
      font-size:12px;
      text-transform:uppercase;
      white-space:nowrap;
      overflow:hidden;
      text-overflow:ellipsis;
    }
    .tag{
      font-weight:900;
      letter-spacing:.10em;
      font-size:12px;
      padding:5px 10px;
      border-radius:999px;
      border:1px solid rgba(42,50,64,.8);
      background: rgba(0,0,0,.25);
    }
    .tag.ok{color: var(--ok); box-shadow: 0 0 0 1px rgba(34,197,94,.12) inset}
    .tag.warn{color: var(--warn)}
    .tag.bad{color: var(--bad)}
    .uptime{
      margin-top:12px;
      display:flex; align-items:center; justify-content:space-between;
      padding:10px 12px;
      border-radius: 12px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(245,158,11,.05), rgba(0,0,0,.25));
    }
    .uptime .value{
      font-weight:900;
      font-variant-numeric: tabular-nums;
      letter-spacing:.08em;
      color:#f8d58b;
    }

    /* "logo" box */
    .cpBox{
      margin: 10px auto 12px;
      display:flex;
      align-items:center;
      justify-content:center;
      gap:10px;
      padding:10px 12px;
      width: fit-content;
      border-radius: 999px;
      border:1px solid rgba(42,50,64,.7);
      background: rgba(0,0,0,.18);
      box-shadow: 0 0 0 1px rgba(239,68,68,.06) inset;
    }
    .dot{
      width:12px;height:12px;border-radius:50%;
      background: radial-gradient(circle at 30% 30%, #ff7aa3, #d10a52);
      box-shadow: 0 0 22px rgba(236,72,153,.35);
    }
    .cpBox span{
      font-weight:900;
      letter-spacing:.12em;
      font-size:12px;
      opacity:.9;
    }

    /* Right side: top blocks */
    .rightTop{
      display:grid;
      grid-template-rows: auto 1fr;
      gap: var(--gutter);
    }

    .wanGrid{
      display:grid;
      grid-template-columns: 1.05fr 1.05fr 0.9fr 1.7fr;
      gap: var(--gutter);
      align-items:stretch;
    }

    .miniBar{
      height: 164px;
      display:flex;
      flex-direction:column;
    }
    .miniBar .miniHdr{
      display:flex; align-items:center; justify-content:space-between;
      padding:10px 12px;
      border-bottom:1px solid rgba(42,50,64,.55);
      position:relative;
      z-index:1;
    }
    .miniHdr .name{
      font-weight:900;
      letter-spacing:.10em;
      font-size:12px;
      text-transform:uppercase;
    }
    .miniHdr .state{
      font-weight:900;
      letter-spacing:.10em;
      font-size:11px;
      color: var(--ok);
    }
    .miniBar .barArea{
      padding: 12px 12px 10px;
      flex:1;
      position:relative;
      z-index:1;
      display:flex;
      align-items:flex-end;
      gap:10px;
    }
    .barFill{
      width:100%;
      border-radius: 10px;
      border:1px solid rgba(42,50,64,.7);
      background: rgba(0,0,0,.18);
      overflow:hidden;
      position:relative;
      height: 100%;
      display:flex;
      align-items:flex-end;
    }
    .barFill > i{
      display:block;
      width:100%;
      height:60%;
      background: linear-gradient(180deg, rgba(34,197,94,.40), rgba(34,197,94,.14));
      box-shadow: 0 0 0 1px rgba(34,197,94,.08) inset;
    }
    .barFill.blue > i{
      background: linear-gradient(180deg, rgba(56,189,248,.40), rgba(56,189,248,.14));
      box-shadow: 0 0 0 1px rgba(56,189,248,.10) inset;
    }
    .barFill.amber > i{
      background: linear-gradient(180deg, rgba(245,158,11,.35), rgba(245,158,11,.12));
      box-shadow: 0 0 0 1px rgba(245,158,11,.10) inset;
    }
    .miniBar .miniFtr{
      padding: 0 12px 12px;
      color: var(--muted);
      font-size:12px;
      position:relative;
      z-index:1;
      display:flex; align-items:center; justify-content:flex-end;
      font-variant-numeric: tabular-nums;
    }

    /* Chart area (static SVG placeholder) */
    .chart{
      height: 164px;
      display:flex;
      flex-direction:column;
    }
    .chart .chartHdr{
      padding:10px 12px;
      border-bottom:1px solid rgba(42,50,64,.55);
      position:relative;
      z-index:1;
      display:flex;
      justify-content:space-between;
      align-items:center;
      gap:12px;
    }
    .chartHdr .name{
      font-weight:900;
      letter-spacing:.10em;
      font-size:12px;
      text-transform:uppercase;
    }
    .legend{
      display:flex;
      flex-wrap:wrap;
      gap:10px 12px;
      justify-content:flex-end;
      color:var(--muted);
      font-size:11px;
    }
    .legItem{display:flex; align-items:center; gap:6px; white-space:nowrap}
    .swatch{
      width:10px;height:10px;border-radius:3px;
      border:1px solid rgba(42,50,64,.9);
      background:#22c55e;
    }
    .swatch.blue{background:#38bdf8}
    .swatch.amber{background:#f59e0b}
    .swatch.red{background:#ef4444}
    .chart .chartBody{
      flex:1;
      padding: 8px 10px 12px;
      position:relative;
      z-index:1;
    }
    .chartSvg{
      width:100%;
      height:100%;
      border-radius: 14px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(0,0,0,.12), rgba(0,0,0,.28));
    }
    .gridLine{stroke: rgba(148,163,184,.18); stroke-width:1}
    .axisText{fill: rgba(148,163,184,.65); font-size:10px; font-family: var(--font)}
    .lineGreen{stroke:#22c55e; stroke-width:2; fill:none; filter:url(#glowG)}
    .lineBlue{stroke:#38bdf8; stroke-width:2; fill:none; filter:url(#glowB)}
    .lineAmber{stroke:#f59e0b; stroke-width:2; fill:none; filter:url(#glowA)}
    .areaGreen{fill: rgba(34,197,94,.18)}
    .areaBlue{fill: rgba(56,189,248,.12)}

    /* Mid title spanning right side */
    .midTitle{
      grid-column: 1 / -1;
      text-align:center;
      padding: 14px 16px;
      text-transform:uppercase;
      font-weight:900;
      letter-spacing:.12em;
      border-radius: var(--radius);
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(255,255,255,.04), rgba(0,0,0,.18));
      box-shadow: var(--shadow);
    }

    /* Bottom grid (details) */
    .detailsGrid{
      display:grid;
      grid-template-columns: 1.2fr 1fr 1.6fr;
      gap: var(--gutter);
      margin-top: var(--gutter);
    }

    .kvGrid{
      display:grid;
      grid-template-columns: repeat(3, 1fr);
      gap: 14px;
      margin-bottom: 14px;
    }
    .kv{
      padding: 12px 12px 10px;
      border-radius: 14px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(0,0,0,.10), rgba(0,0,0,.25));
      position:relative;
      z-index:1;
      min-height:74px;
    }
    .kv .k{
      color: var(--muted);
      font-size:11px;
      letter-spacing:.10em;
      text-transform:uppercase;
      margin-bottom:8px;
      display:flex; align-items:center; gap:8px;
    }
    .kv .v{
      font-weight:900;
      letter-spacing:.04em;
      font-size:14px;
      opacity:.95;
      white-space:nowrap;
      overflow:hidden;
      text-overflow:ellipsis;
    }

    .ringRow{
      display:grid;
      grid-template-columns: repeat(2, 1fr);
      gap:14px;
      margin-bottom: 14px;
    }
    .ring{
      padding: 12px;
      border-radius: 14px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(0,0,0,.10), rgba(0,0,0,.25));
      position:relative;
      z-index:1;
      display:flex;
      align-items:center;
      gap:14px;
      min-height:92px;
    }
    .ring svg{flex:0 0 auto}
    .ring .meta{min-width:0}
    .ring .meta .k{
      color: var(--muted);
      font-size:11px;
      letter-spacing:.10em;
      text-transform:uppercase;
      margin-bottom:6px;
    }
    .ring .meta .v{
      font-weight:900;
      font-size:14px;
      letter-spacing:.06em;
      font-variant-numeric: tabular-nums;
    }

    .miniCharts{
      display:grid;
      grid-template-columns: repeat(2, 1fr);
      gap: var(--gutter);
    }
    .miniChart{
      height: 190px;
      display:flex;
      flex-direction:column;
    }

    .services{
      display:grid;
      grid-template-columns: repeat(2, 1fr);
      gap:10px;
    }
    .svc{
      display:flex;
      align-items:center;
      justify-content:space-between;
      padding:10px 12px;
      border-radius: 12px;
      border:1px solid rgba(42,50,64,.7);
      background: rgba(0,0,0,.20);
      position:relative;
      z-index:1;
    }
    .svc .name{
      font-weight:800;
      letter-spacing:.06em;
      font-size:12px;
      text-transform:uppercase;
      color:#dbe4ff;
    }
    .mark{
      width:18px;height:18px;border-radius:6px;
      display:grid; place-items:center;
      border:1px solid rgba(42,50,64,.85);
      font-weight:900;
      font-size:12px;
    }
    .mark.ok{color:var(--ok); box-shadow: 0 0 0 1px rgba(34,197,94,.10) inset}
    .mark.bad{color:var(--bad); box-shadow: 0 0 0 1px rgba(239,68,68,.12) inset}

    .severityRow{
      display:grid;
      grid-template-columns: repeat(5, 1fr);
      gap: 12px;
      margin-top: var(--gutter);
    }
    .sev{
      min-height: 112px;
      padding: 12px 12px;
      border-radius: 16px;
      border:1px solid rgba(42,50,64,.7);
      background: linear-gradient(180deg, rgba(0,0,0,.10), rgba(0,0,0,.28));
      position:relative;
      z-index:1;
      display:flex;
      flex-direction:column;
      justify-content:space-between;
      overflow:hidden;
    }
    .sev .n{
      font-weight:900;
      font-size: 28px;
      letter-spacing:.08em;
      font-variant-numeric: tabular-nums;
      opacity:.9;
    }
    .sev .k{
      color: var(--muted);
      text-transform:uppercase;
      letter-spacing:.12em;
      font-size:11px;
      font-weight:800;
    }
    .sev::after{
      content:"";
      height:5px;
      width:100%;
      position:absolute;
      left:0; bottom:0;
      opacity:.9;
      background: rgba(148,163,184,.35);
    }
    .sev.disaster::after{background: rgba(239,68,68,.85)}
    .sev.high::after{background: rgba(245,158,11,.9)}
    .sev.average::after{background: rgba(250,204,21,.9)}
    .sev.warning::after{background: rgba(56,189,248,.9)}
    .sev.info::after{background: rgba(34,197,94,.9)}

    /* Responsive */
    @media (max-width: 1180px){
      .grid{grid-template-columns: 1fr}
      .wanGrid{grid-template-columns: 1fr 1fr}
      .detailsGrid{grid-template-columns: 1fr}
      .severityRow{grid-template-columns: 1fr 1fr}
      .kvGrid{grid-template-columns: 1fr}
      .ringRow{grid-template-columns: 1fr}
      .miniCharts{grid-template-columns: 1fr}
    }
    @media (max-width: 560px){
      .topbar{flex-direction:column; gap:10px}
      .wanGrid{grid-template-columns: 1fr}
      .severityRow{grid-template-columns: 1fr}
    }

options.onRender:
(() => {
  if (!htmlNode || !data || !data.series) return;

  const el = (q) => htmlNode.querySelector(q);
  const setText = (q, v) => { const n = el(q); if(n) n.innerHTML = v; };

  const last = (series) => {
    const n = series.fields.find(f => f.type === 'number');
    if (n && n.values.length) return n.values.get(n.values.length - 1);
    const s = series.fields.find(f => f.type === 'string');
    if (s && s.values.length) return s.values.get(s.values.length - 1);
    return null;
  };

  const findLastByName = (name) => {
    const s = data.series.find(x => x.name === name);
    return s ? last(s) : null;
  };

  const toNum = (v) => (isFinite(Number(v)) ? Number(v) : 0);

  const bps = (v) => {
    const x = Number(v ?? 0);
    if (!isFinite(x) || x < 0) return '0 b/s';
    const units = ['b/s','Kb/s','Mb/s','Gb/s','Tb/s'];
    let i=0, val=x;
    while (val >= 1024 && i < units.length-1){ val/=1024; i++; }
    return `${val.toFixed(val >= 100 ? 0 : val >= 10 ? 1 : 2)} ${units[i]}`;
  };

  // Update clock
  const updateClock = () => {
    const now = new Date();
    const h = String(now.getHours()).padStart(2, '0');
    const m = String(now.getMinutes()).padStart(2, '0');
    const s = String(now.getSeconds()).padStart(2, '0');
    const clockEl = el('.clock');
    if (clockEl) clockEl.innerHTML = `${h}:${m}:${s}`;
  };
  updateClock();
  setInterval(updateClock, 1000);

  // Atualizar dados do dashboard Check Point
  const cpuUsage = findLastByName('CPU utilization');
  const memUsage = findLastByName('Memory utilization');
  const uptime = findLastByName('System uptime');
  const icmpStatus = findLastByName('ICMP ping');
  const snmpStatus = findLastByName('SNMP agent availability');

  // Atualizar CPU
  const cpuEl = el('.cpu-value');
  if (cpuEl && cpuUsage !== null) {
    cpuEl.innerHTML = `${toNum(cpuUsage).toFixed(0)}%`;
  }

  // Atualizar Mem√≥ria
  const memEl = el('.mem-value');
  if (memEl && memUsage !== null) {
    memEl.innerHTML = `${toNum(memUsage).toFixed(0)}%`;
  }

  // Atualizar status ICMP
  const icmpEl = el('.icmp-status');
  if (icmpEl && icmpStatus !== null) {
    icmpEl.innerHTML = toNum(icmpStatus) === 1 ? 'UP' : 'DOWN';
    icmpEl.className = toNum(icmpStatus) === 1 ? 'status-badge up' : 'status-badge down';
  }

  // Atualizar status SNMP
  const snmpEl = el('.snmp-status');
  if (snmpEl && snmpStatus !== null) {
    snmpEl.innerHTML = toNum(snmpStatus) === 1 ? 'UP' : 'DOWN';
    snmpEl.className = toNum(snmpStatus) === 1 ? 'status-badge up' : 'status-badge down';
  }

  // Atualizar uptime
  const uptimeEl = el('.uptime-value');
  if (uptimeEl && uptime !== null) {
    const seconds = toNum(uptime);
    const days = Math.floor(seconds / 86400);
    const hours = Math.floor((seconds % 86400) / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    uptimeEl.innerHTML = `${days}D ${hours}H ${minutes}M`;
  }

  // Processar interfaces WAN
  const rxRe = /^Interface (.+): Bits received$/i;
  const txRe = /^Interface (.+): Bits sent$/i;

  const byIf = new Map();
  const ensure = (ifn) => {
    if (!byIf.has(ifn)) byIf.set(ifn, { rx:0, tx:0, status:'UP' });
    return byIf.get(ifn);
  };

  for (const s of data.series) {
    const name = s.name || '';
    let m;
    if ((m = name.match(rxRe))) ensure(m[1].trim()).rx = toNum(last(s));
    else if ((m = name.match(txRe))) ensure(m[1].trim()).tx = toNum(last(s));
  }

  // Atualizar WAN Links
  const wanInterfaces = ['WAN 4 (VIVO)', 'WAN 5 (CLARO)', 'CAMON'];
  wanInterfaces.forEach((wanName, idx) => {
    const data = byIf.get(wanName);
    if (data) {
      const speedEl = el(`.wan-speed-${idx}`);
      if (speedEl) speedEl.innerHTML = bps(data.rx + data.tx);
    }
  });

})();
