      }

      .service-card.status-down-card {
        border-left: 4px solid #c62828;
      }

      .service-card.status-degraded-card {
        border-left: 4px solid #e65100;
      }

      .logo-wrapper {
        height: 80px;
        display: flex;
        align-items: center;
        justify-content: center;
        margin-bottom: 15px;
        font-size: 48px;
      }

      .service-name {
        font-size: 18px;
