        margin-bottom: 15px;
      }

      .sparkline-container {
        width: 76%;
        margin: 0 auto;
        height: 40px;
        display: flex;
        align-items: flex-end;
        justify-content: space-between;
        gap: 2px;
        margin-top: auto;
      }

      .sparkline-bar {
        flex: 1;
        background: linear-gradient(180deg, #4CAF50 0%, #45a049 100%);
        border-radius: 2px 2px 0 0;
        min-height: 3px;
