      }

      .sparkline-bar.warning {
        background: linear-gradient(180deg, #ff9800 0%, #f57c00 100%);
      }

      .sparkline-bar.danger {
        background: linear-gradient(180deg, #f44336 0%, #d32f2f 100%);
      }

      .status-badge {
        display: inline-block;
        padding: 4px 12px;
        border-radius: 12px;
        font-size: 11px;
        font-weight: 600;
        text-transform: uppercase;
        margin-top: 10px;
      }

      .status-operational {
        background: #e8f5e9;
        color: #2e7d32;
      }

      .status-degraded {
        background: #fff3e0;
        color: #e65100;
      }

      .status-down {
        background: #ffebee;
        color: #c62828;
      }

      .footer {
        background-color: #3c4043;
        color: #bdc1c6;
        padding: 20px;
        text-align: center;
        font-size: 14px;
        margin-top: 30px;
      }

      .footer a {
        color: #eee;
        text-decoration: none;
      }

      .footer a:hover {
        color: #e94334;
      }

      @media (max-width: 768px) {
        .hero h1 {
          font-size: 1.5rem;
        }
        .service-grid {
          grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
          gap: 15px;
        }
      }

      @media (min-width: 1400px) {
        .service-grid {
          grid-template-columns: repeat(6, 1fr);
        }
      }

      @media (min-width: 1920px) {
        .main-content {
          max-width: 1800px;
        }
        .service-grid {
          grid-template-columns: repeat(8, 1fr);
        }
        .hero h1 {
          font-size: 2.5rem;
        }
        .service-name {
          font-size: 20px;
        }
      }
    </style>
  `;

  // Parse data from Grafana
  const panelData = (typeof data !== 'undefined' && data) || (typeof panel !== 'undefined' && panel.data) || null;
  const series = panelData && Array.isArray(panelData.series) ? panelData.series : [];

  // const toNumber = (value) => {
  //   if (value == null) return null;
  //   if (typeof value === 'number') return Number.isFinite(value) ? value : null;
  //   if (typeof value === 'string' && value.trim() !== '') {
  //     const num = Number(value);
  //     return Number.isFinite(num) ? num : null;
  //   }
  //   return null;
  // };

  const toNumber = (value) => {
    if (value == null) return null;
    if (typeof value === 'number') return Number.isFinite(value) ? value : null;
    if (typeof value === 'string' && value.trim() !== '') {
      let str = value.trim().toLowerCase();
      let multiplier = 1;

      // Parseia unidades de tempo
      if (str.endsWith('ms')) {
        str = str.replace('ms', '').trim();
        multiplier = 0.001; // milissegundos para segundos
      } else if (str.endsWith('s')) {
        str = str.replace('s', '').trim();
        multiplier = 1; // j√° est√° em segundos
      }

      const num = parseFloat(str);
      return Number.isFinite(num) ? num * multiplier : null;
    }
    return null;
  };

  const getLastValue = (frame) => {
    if (!frame || !frame.fields) return null;
    const valueField = frame.fields.find(f => f.type === 'number') || frame.fields[1];
    if (!valueField) return null;
    const vals = valueField.values;
    if (vals && typeof vals.get === 'function') {
      const len = typeof vals.length === 'number' ? vals.length : (typeof vals.toArray === 'function' ? vals.toArray().length : 0);
      return len ? vals.get(len - 1) : null;
    }
    if (Array.isArray(vals)) return vals.length ? vals[vals.length - 1] : null;
    return null;
  };

  const getAllValues = (frame) => {
    if (!frame || !frame.fields) return [];
    const valueField = frame.fields.find(f => f.type === 'number') || frame.fields[1];
    if (!valueField) return [];
    const vals = valueField.values;
    if (vals && typeof vals.toArray === 'function') return vals.toArray();
    if (Array.isArray(vals)) return vals;
    return [];
  };

  const getHost = (frame) => {
    const f = frame && frame.fields ? frame.fields[1] : null;
    const labels = (f && f.labels) || frame.labels || (frame.meta && frame.meta.custom) || {};
    const byLabel = labels.host || labels.Host || labels.hostname || null;
    if (byLabel) return byLabel.replace(/:+$/, '').trim();

    let name = String(frame && frame.name ? frame.name : '');
    const sslMatch = name.match(/^(SITE\s+)?(.+?):\s*(Expires on|Valid from)/i);
    if (sslMatch) return 'SITE ' + sslMatch[2].trim();
    const websiteMatch = name.match(/^(SITE\s+)?(.+?):\s*Website/i);
    if (websiteMatch) return 'SITE ' + websiteMatch[2].trim();
    const simpleMatch = name.match(/^(SITE\s+)?([A-Z\s]+)/i);
    if (simpleMatch && simpleMatch[2]) return 'SITE ' + simpleMatch[2].trim().replace(/:+$/, '');
    return 'UNKNOWN';
  };

  const byRef = {};
  series.forEach(s => {
    const ref = s.refId || 'unknown';
    (byRef[ref] = byRef[ref] || []).push(s);
  });

  const indexByHost = (frames, getAllVals = false) => {
    const map = {};
    (frames || []).forEach(f => {
      const h = getHost(f);
      if (h && h !== 'UNKNOWN') {
        if (getAllVals) {
          map[h] = getAllValues(f).map(v => toNumber(v)).filter(v => v != null);
        } else {
          let v = getLastValue(f);
          v = toNumber(v) ?? v;
          if (v != null) map[h] = v;
        }
      }
    });
    return map;
  };

  // Get metrics
  const ping = indexByHost(byRef['A']);
  const status = indexByHost(byRef['B']);
  const httpRt = indexByHost(byRef['C']);
  const httpRtHistory = indexByHost(byRef['C'], true);
  const navLoad = indexByHost(byRef['F']);
  const navLoadHistory = indexByHost(byRef['F'], true);

  // Service configuration with emojis
  const serviceConfig = {
    'SITE SEFAZ': { name: 'SEFAZ', fullName: 'Secretaria da Fazenda', url: 'sefaz.rr.gov.br', emoji: 'üí∞' },
    'SITE SESP': { name: 'SESP', fullName: 'Secretaria de Seguran√ßa P√∫blica', url: 'sesp.rr.gov.br', emoji: 'üõ°Ô∏è' },
    'SITE EDUCACAO': { name: 'EDUCA√á√ÉO', fullName: 'Secretaria de Educa√ß√£o', url: 'educacao.rr.gov.br', emoji: 'üìö' },
    'SITE OUVIDORIA': { name: 'OUVIDORIA', fullName: 'Ouvidoria do Estado', url: 'ouvidoria.rr.gov.br', emoji: 'üì¢' },
    'SITE SEINF': { name: 'SEINF', fullName: 'Secretaria de Infraestrutura', url: 'seinf.rr.gov.br', emoji: 'üèóÔ∏è' },
    'SITE SETRABES': { name: 'SETRABES', fullName: 'Secretaria do Trabalho', url: 'setrabes.rr.gov.br', emoji: 'ü§ù' },
    'SITE FAPERR': { name: 'FAPERR', fullName: 'Funda√ß√£o de Amparo √† Pesquisa', url: 'faperr.rr.gov.br', emoji: 'üî¨' },
    'SITE SEPLAN': { name: 'SEPLAN', fullName: 'Secretaria de Planejamento', url: 'seplan.rr.gov.br', emoji: 'üìä' },
    'SITE SEED': { name: 'SEED', fullName: 'Secretaria de Desenvolvimento', url: 'seed.rr.gov.br', emoji: 'üìà' },
    'SITE POLICIA CIVIL': { name: 'POL√çCIA CIVIL', fullName: 'Pol√≠cia Civil de Roraima', url: 'policiacivil.rr.gov.br', emoji: 'üëÆ' },
    'SITE PGE': { name: 'PGE', fullName: 'Procuradoria Geral do Estado', url: 'www.pge.rr.gov.br', emoji: '‚öñÔ∏è' },
    'SITE SECULT': { name: 'SECULT', fullName: 'Secretaria de Cultura', url: 'secult.rr.gov.br', emoji: 'üé≠' },
    'SITE TRANSPARENCIA': { name: 'TRANSPAR√äNCIA', fullName: 'Portal da Transpar√™ncia', url: 'www.transparencia.rr.gov.br', emoji: 'üîç' },
    'SITE ADERR': { name: 'ADERR', fullName: 'Ag√™ncia de Desenvolvimento', url: 'www.aderr.rr.gov.br', emoji: 'üå±' },
    'SITE CODESAIMA': { name: 'CODESAIMA', fullName: 'Companhia de Desenvolvimento', url: 'codesaima.rr.gov.br', emoji: 'üå≥' },
    'SITE IPEM': { name: 'IPEM', fullName: 'Instituto de Pesos e Medidas', url: 'ipem.rr.gov.br', emoji: '‚öñÔ∏è' },
    'SITE IMPRENSA OFICIAL': { name: 'IMPRENSA OFICIAL', fullName: 'IMPRENSA OFICIAL', url: 'imprensaoficial.rr.gov.br', emoji: 'üì∞' }
  };

  // Build services list
  const hosts = new Set();
  [ping, status, httpRt, navLoad].forEach(m => {
    Object.keys(m || {}).forEach(h => hosts.add(h));
  });

  let services = Array.from(hosts).map(h => {
    const config = serviceConfig[h] || { 
      name: h.replace(/^SITE\s+/i, ''), 
      fullName: h.replace(/^SITE\s+/i, ''),
      url: '', 
      emoji: 'üèõÔ∏è' 
    };

    const pingVal = toNumber(ping[h]);
    const statusVal = toNumber(status[h]);
    const hasPing = pingVal != null;
    const hasStatus = statusVal != null;
    const isUp = (hasPing && pingVal > 0) || (hasStatus && statusVal >= 200 && statusVal < 400);

    const respTimeRaw = toNumber(httpRt[h]) || 0;
    const respTime = Math.round(respTimeRaw * 1000);
    const loadTimeRaw = toNumber(navLoad[h]) || 0;
    const loadTime = Math.round(loadTimeRaw * 1000);

    // Determine status
    let statusLabel = 'operational';
    if (!isUp && (hasPing || hasStatus)) {
      statusLabel = 'down';
    } else if (loadTime > 5000 || respTime > 3000) {
      statusLabel = 'degraded';
    }

 // Get history for sparkline (last 12 points)
  let history = navLoadHistory[h] || httpRtHistory[h] || [];
  // ‚úÖ CORRE√á√ÉO: Se n√£o houver dados, usar zeros (n√£o valores aleat√≥rios)
  if (history.length === 0) {
    history = Array(12).fill(0);  // ‚úÖ Zeros representam "sem dados"
  }
  history = history.slice(-12);
  while (history.length < 12) {
    history.unshift(0);  // ‚úÖ Preencher com zeros, n√£o com o primeiro valor
  }

    return {
      host: h,
      name: config.name,
      fullName: config.fullName,
      url: config.url,
      emoji: config.emoji,
      status: statusLabel,
      isUp: statusLabel === 'operational',
      isDegraded: statusLabel === 'degraded',
      isDown: statusLabel === 'down',
      respTime: respTime,
      loadTime: loadTime,
      history: history
    };
  }).filter(s => s.host !== 'UNKNOWN');

  // Sort: DOWN first, then DEGRADED, then UP
  services.sort((a, b) => {
    const order = { 'down': 0, 'degraded': 1, 'operational': 2 };
    return order[a.status] - order[b.status];
  });

  const now = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit' });

  // Generate sparkline bars
  const generateSparkline = (history, isDown) => {
    const maxVal = Math.max(...history, 0.001);
    return history.map((val, idx) => {
      const height = Math.max((val / maxVal) * 100, 10);
      let barClass = '';
      if (isDown) {
        barClass = 'danger';
      } else if (val > maxVal * 0.7) {
        barClass = 'warning';
      }
      return `<div class="sparkline-bar ${barClass}" style="height: ${height}%"></div>`;
    }).join('');
  };

  // Render services
  const servicesHtml = services.length > 0 ? services.map(s => {
    const cardClass = s.isDown ? 'status-down-card' : (s.isDegraded ? 'status-degraded-card' : '');
    const badgeClass = s.isDown ? 'status-down' : (s.isDegraded ? 'status-degraded' : 'status-operational');
    const badgeText = s.isDown ? '‚úó Fora do Ar' : (s.isDegraded ? '‚ö† Degradado' : '‚úì Operacional');

    return `
      <div class="service-card ${cardClass}" onclick="window.open('https://${s.url}', '_blank')">
        <div class="logo-wrapper">
          ${s.emoji}
        </div>
        <div class="service-name">${s.name}</div>
        <div class="service-url">${s.url}</div>

        <div class="sparkline-container">
          ${generateSparkline(s.history, s.isDown)}
        </div>

        <div class="status-badge ${badgeClass}">
          ${badgeText}
        </div>
      </div>
    `;
  }).join('') : `
    <div style="grid-column: 1/-1; text-align: center; padding: 60px; color: #6c757d;">
      <div style="font-size: 48px; margin-bottom: 16px;">üì°</div>
      <h3 style="color: #212529; margin-bottom: 8px;">Aguardando dados...</h3>
      <p>Os dados dos servi√ßos ser√£o exibidos assim que estiverem dispon√≠veis.</p>
    </div>
  `;

  htmlNode.innerHTML = styles + `
    <div class="govmonitor-container">
      <nav class="navbar">
        <div class="navbar-content">
          <div class="logo">
            GovDown Detector - SEFAZ RR
            <div class="logo-subtitle">Status dos Servi√ßos Governamentais</div>
          </div>
          <div class="last-update">
            √öltima atualiza√ß√£o: ${now}
          </div>
        </div>
      </nav>

      <section class="hero">
        <h1>Status em tempo real dos servi√ßos governamentais</h1>
        <p>Monitoramos os sites do Governo de Roraima em tempo real e informamos se est√£o fora do ar ou com problemas.</p>
      </section>

      <main class="main-content">

        <div class="service-grid">
          ${servicesHtml}
        </div>
      </main>

      <footer class="footer">
        <p>&copy; 2024 Governo do Estado de Roraima - Monitoramento Interno</p>
        <p style="margin-top: 10px;">Atualiza√ß√£o autom√°tica via Grafana</p>
      </footer>
    </div>
  `;
})();
