/********************************************************************************************
 * GovDetector RR - Clone do DownDetector
 * Dashboard simples para tel√£o - Sites Governamentais de Roraima
 ********************************************************************************************/
(() => {
  if (!htmlNode) return;

  const styles = `
    <style>
      @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');

      .govdetector-container {
        background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
        font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
        padding: 24px;
        min-height: 100%;
        display: flex;
        flex-direction: column;
      }

      .header {
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin-bottom: 24px;
        padding-bottom: 16px;
        border-bottom: 2px solid #dee2e6;
      }

      .header-left {
        display: flex;
        align-items: center;
        gap: 16px;
      }

      .header-logo {
        width: 48px;
        height: 48px;
        background: linear-gradient(135deg, #198754 0%, #20c997 100%);
        border-radius: 12px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 24px;
        box-shadow: 0 4px 12px rgba(25, 135, 84, 0.3);
      }

      .header-title {
        font-size: 28px;
        font-weight: 700;
        color: #212529;
        margin: 0;
      }

      .header-subtitle {
        font-size: 14px;
        color: #6c757d;
        margin: 0;
      }

      .header-right {
        display: flex;
        align-items: center;
        gap: 16px;
      }

      .status-summary {
        display: flex;
        align-items: center;
        gap: 8px;
