      }

      .status-summary.all-ok {
        background: rgba(25, 135, 84, 0.1);
        color: #198754;
      }

      .status-summary.has-issues {
        background: rgba(220, 53, 69, 0.1);
        color: #dc3545;
      }

      .status-dot {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        animation: pulse 2s infinite;
      }

      .status-dot.green {
        background: #198754;
      }

      .status-dot.red {
        background: #dc3545;
        animation: pulse-red 1.5s infinite;
      }

      @keyframes pulse {
        0%, 100% { opacity: 1; transform: scale(1); }
        50% { opacity: 0.7; transform: scale(1.1); }
      }

      @keyframes pulse-red {
        0%, 100% { box-shadow: 0 0 0 0 rgba(220, 53, 69, 0.7); }
        50% { box-shadow: 0 0 0 8px rgba(220, 53, 69, 0); }
      }

      .update-time {
        font-size: 13px;
        color: #6c757d;
        background: #fff;
        padding: 8px 12px;
        border-radius: 6px;
        border: 1px solid #dee2e6;
      }

      .services-grid {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(220px, 1fr));
        gap: 20px;
        flex: 1;
      }

      .service-card {
        background: #fff;
        border: 1px solid #e9ecef;
        border-radius: 12px;
        padding: 14px;
        padding-top: 34px;
        transition: all 0.3s ease;
        cursor: pointer;
        position: relative;
        overflow: hidden;
      }

      .service-card:hover {
        transform: translateY(-4px);
        box-shadow: 0 12px 24px rgba(0, 0, 0, 0.1);
        border-color: #dee2e6;
      }

      .service-card.status-down {
        border-color: #dc3545;
        background: linear-gradient(135deg, #fff 0%, #fff5f5 100%);
      }

      .service-card.status-degraded {
        border-color: #ffc107;
        background: linear-gradient(135deg, #fff 0%, #fffef5 100%);
      }

      .service-header {
        display: flex;
        align-items: flex-start;
        min-height: 64px;
        gap: 16px;
        margin-bottom: 16px;
      }

      .service-logo {
        width: 56px;
        height: 56px;
        background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
        border-radius: 12px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 12px;
        font-weight: 700;
        color: #495057;
        border: 2px solid #e9ecef;
        text-align: center;
        line-height: 1.2;
        padding: 4px;
      }

      .service-logo.gov {
        background: linear-gradient(135deg, #0d6efd 0%, #0dcaf0 100%);
        color: #fff;
        border: none;
      }

      .service-info {
        flex: 1;
      }

      .service-name {
        font-size: 14px;
        font-weight: 600;
        color: #212529;
        line-height: 1.2;
        margin: 0 0 4px 0;
        display: -webkit-box;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
      }

      .service-url {
        font-size: 12px;
        color: #6c757d;
        margin: 0;
        text-decoration: none;
      }

      .service-status-badge {
        position: absolute;
        top: 10px;
        right: 10px;
        z-index: 2;
        padding: 4px 10px;
        border-radius: 20px;
        font-size: 11px;
        font-weight: 600;
        text-transform: uppercase;
      }

      .service-status-badge.up {
        background: rgba(25, 135, 84, 0.1);
        color: #198754;
      }

      .service-status-badge.down {
        background: rgba(220, 53, 69, 0.15);
        color: #dc3545;
      }

      .service-status-badge.degraded {
        background: rgba(255, 193, 7, 0.2);
        color: #997404;
      }

      .sparkline-container {
        margin-top: 12px;
        padding-top: 12px;
        border-top: 1px solid #f1f3f4;
      }

      .sparkline-label {
        display: flex;
        justify-content: space-between;
        font-size: 11px;
        color: #adb5bd;
        margin-bottom: 6px;
      }

      .sparkline {
        display: flex;
        align-items: flex-end;
        gap: 2px;
        height: 40px;
        background: #f8f9fa;
        border-radius: 6px;
        padding: 6px;
      }

      .sparkline-bar {
        flex: 1;
        min-width: 3px;
        border-radius: 2px 2px 0 0;
        transition: all 0.3s ease;
      }

      .sparkline-bar:hover {
        opacity: 0.8;
        transform: scaleY(1.1);
      }

      .sparkline-bar.ok {
        background: linear-gradient(180deg, #20c997 0%, #198754 100%);
      }

      .sparkline-bar.warning {
        background: linear-gradient(180deg, #ffc107 0%, #fd7e14 100%);
      }

      .sparkline-bar.error {
        background: linear-gradient(180deg, #f8d7da 0%, #dc3545 100%);
      }

      .service-metrics {
        display: flex;
        justify-content: space-between;
        margin-top: 12px;
        padding-top: 12px;
        border-top: 1px solid #f1f3f4;
      }

      .metric {
        text-align: center;
      }

      .metric-value {
        font-size: 16px;
        font-weight: 700;
        color: #212529;
      }

      .metric-value.good {
        color: #198754;
      }

      .metric-value.warning {
        color: #ffc107;
      }

      .metric-value.bad {
        color: #dc3545;
      }

      .metric-label {
        font-size: 10px;
        color: #6c757d;
        text-transform: uppercase;
        letter-spacing: 0.5px;
      }

      .footer {
        margin-top: 24px;
        padding-top: 16px;
        border-top: 1px solid #dee2e6;
        display: flex;
        justify-content: space-between;
        align-items: center;
        font-size: 12px;
        color: #6c757d;
      }

      .footer-legend {
        display: flex;
        gap: 20px;
      }

      .legend-item {
        display: flex;
        align-items: center;
        gap: 6px;
      }

      .legend-dot {
        width: 12px;
        height: 12px;
        border-radius: 3px;
      }

      .legend-dot.green {
        background: linear-gradient(180deg, #20c997 0%, #198754 100%);
      }

      .legend-dot.yellow {
        background: linear-gradient(180deg, #ffc107 0%, #fd7e14 100%);
      }

      .legend-dot.red {
        background: linear-gradient(180deg, #f8d7da 0%, #dc3545 100%);
      }

      @media (max-width: 768px) {
        .services-grid {
          grid-template-columns: 1fr;
        }
        .header {
          flex-direction: column;
          gap: 16px;
          text-align: center;
        }
        .header-left {
          flex-direction: column;
        }
      }
    </style>
  `;

  // Parse data from Grafana
  const panelData = (typeof data !== 'undefined' && data) || (typeof panel !== 'undefined' && panel.data) || null;
  const series = panelData && Array.isArray(panelData.series) ? panelData.series : [];

  const toNumber = (value) => {
    if (value == null) return null;
    if (typeof value === 'number') return Number.isFinite(value) ? value : null;
    if (typeof value === 'string' && value.trim() !== '') {
      const num = Number(value);
      return Number.isFinite(num) ? num : null;
    }
    return null;
  };

  const getLastValue = (frame) => {
    if (!frame || !frame.fields) return null;
    const valueField = frame.fields.find(f => f.type === 'number') || frame.fields[1];
    if (!valueField) return null;
    const vals = valueField.values;
    if (vals && typeof vals.get === 'function') {
      const len = typeof vals.length === 'number' ? vals.length : (typeof vals.toArray === 'function' ? vals.toArray().length : 0);
      return len ? vals.get(len - 1) : null;
    }
    if (Array.isArray(vals)) return vals.length ? vals[vals.length - 1] : null;
    return null;
  };

  const getAllValues = (frame) => {
    if (!frame || !frame.fields) return [];
    const valueField = frame.fields.find(f => f.type === 'number') || frame.fields[1];
    if (!valueField) return [];
    const vals = valueField.values;
    if (vals && typeof vals.toArray === 'function') return vals.toArray();
    if (Array.isArray(vals)) return vals;
    return [];
  };

  const getHost = (frame) => {
    const f = frame && frame.fields ? frame.fields[1] : null;
    const labels = (f && f.labels) || frame.labels || (frame.meta && frame.meta.custom) || {};
    const byLabel = labels.host || labels.Host || labels.hostname || null;
    if (byLabel) return byLabel.replace(/:+$/, '').trim();

    let name = String(frame && frame.name ? frame.name : '');
    const sslMatch = name.match(/^(SITE\s+)?(.+?):\s*(Expires on|Valid from)/i);
    if (sslMatch) return 'SITE ' + sslMatch[2].trim();
    const websiteMatch = name.match(/^(SITE\s+)?(.+?):\s*Website/i);
    if (websiteMatch) return 'SITE ' + websiteMatch[2].trim();
    const simpleMatch = name.match(/^(SITE\s+)?([A-Z\s]+)/i);
    if (simpleMatch && simpleMatch[2]) return 'SITE ' + simpleMatch[2].trim().replace(/:+$/, '');
    return 'UNKNOWN';
  };

  const byRef = {};
  series.forEach(s => {
    const ref = s.refId || 'unknown';
    (byRef[ref] = byRef[ref] || []).push(s);
  });

  const indexByHost = (frames, getAllVals = false) => {
    const map = {};
    (frames || []).forEach(f => {
      const h = getHost(f);
      if (h && h !== 'UNKNOWN') {
        if (getAllVals) {
          map[h] = getAllValues(f).map(v => toNumber(v)).filter(v => v != null);
        } else {
          let v = getLastValue(f);
          v = toNumber(v) ?? v;
          if (v != null) map[h] = v;
        }
      }
    });
    return map;
  };

  // Get metrics
  const ping = indexByHost(byRef['A']);
  const status = indexByHost(byRef['B']);
  const httpRt = indexByHost(byRef['C']);
  const httpRtHistory = indexByHost(byRef['C'], true);
  const navLoad = indexByHost(byRef['F']);
  const navLoadHistory = indexByHost(byRef['F'], true);

  // Service definitions with logos/initials
  const serviceConfig = {
    'SITE SEFAZ': { name: 'SEFAZ', initials: 'SEFAZ', url: 'sefaz.rr.gov.br', color: '#0d6efd' },
    'SITE SESP': { name: 'SESP', initials: 'SESP', url: 'sesp.rr.gov.br', color: '#6f42c1' },
    'SITE EDUCACAO': { name: 'Educa√ß√£o', initials: 'EDUC', url: 'educacao.rr.gov.br', color: '#20c997' },
    'SITE OUVIDORIA': { name: 'Ouvidoria', initials: 'OUV', url: 'ouvidoria.rr.gov.br', color: '#fd7e14' },
    'SITE SEINF': { name: 'SEINF', initials: 'SEINF', url: 'seinf.rr.gov.br', color: '#0dcaf0' },
    'SITE SETRABES': { name: 'SETRABES', initials: 'SETRA', url: 'setrabes.rr.gov.br', color: '#198754' },
    'SITE FAPERR': { name: 'FAPERR', initials: 'FAPERR', url: 'faperr.rr.gov.br', color: '#dc3545' },
    'SITE SEPLAN': { name: 'SEPLAN', initials: 'SEPLAN', url: 'seplan.rr.gov.br', color: '#6610f2' },
    'SITE SEED': { name: 'SEED', initials: 'SEED', url: 'seed.rr.gov.br', color: '#d63384' },
    'SITE POLICIA CIVIL': { name: 'Pol√≠cia Civil', initials: 'PC', url: 'policiacivil.rr.gov.br', color: '#212529' },
    'SITE PGE': { name: 'PGE', initials: 'PGE', url: 'www.pge.rr.gov.br', color: '#343a40' },
    'SITE SECULT': { name: 'SECULT', initials: 'SECULT', url: 'secult.rr.gov.br', color: '#e83e8c' },
    'SITE TRANSPARENCIA': { name: 'Transpar√™ncia', initials: 'TRANSP', url: 'transparencia.rr.gov.br', color: '#17a2b8' },
    'SITE ADERR': { name: 'ADERR', initials: 'ADERR', url: 'www.aderr.rr.gov.br', color: '#28a745' },
    'SITE CODESAIMA': { name: 'CODESAIMA', initials: 'CODES', url: 'codesaima.rr.gov.br', color: '#ffc107' },
    'SITE IPEM': { name: 'IPEM', initials: 'IPEM', url: 'ipem.rr.gov.br', color: '#007bff' },
    'Imprensa Oficial': { name: 'Imprensa Oficial', initials: 'IMPR', url: 'imprensaoficial.rr.gov.br', color: '#6c757d' }
  };

  // Build services list
  const hosts = new Set();
  [ping, status, httpRt, navLoad].forEach(m => {
    Object.keys(m || {}).forEach(h => hosts.add(h));
  });

  let services = Array.from(hosts).map(h => {
    const config = serviceConfig[h] || { name: h.replace(/^SITE\s+/i, ''), initials: h.replace(/^SITE\s+/i, '').substring(0, 4).toUpperCase(), url: '', color: '#6c757d' };

    const pingVal = toNumber(ping[h]);
    const statusVal = toNumber(status[h]);
    const hasPing = pingVal != null;
    const hasStatus = statusVal != null;
    const isUp = (hasPing && pingVal > 0) || (hasStatus && statusVal >= 200 && statusVal < 400);

    const respTimeRaw = toNumber(httpRt[h]) || 0;
    const respTime = Math.round(respTimeRaw * 1000);
    const loadTimeRaw = toNumber(navLoad[h]) || 0;
    const loadTime = Math.round(loadTimeRaw * 1000);

    // Determine status
    let statusLabel = 'UP';
    let isDegraded = false;
    if (!isUp && (hasPing || hasStatus)) {
      statusLabel = 'DOWN';
    } else if (loadTime > 5000 || respTime > 3000) {
      statusLabel = 'DEGRADED';
      isDegraded = true;
    }

    // Get history for sparkline (use load time or response time)
    let history = navLoadHistory[h] || httpRtHistory[h] || [];
    if (history.length === 0) {
      // Generate placeholder data if no history
      history = Array(24).fill(0).map(() => Math.random() * 2);
    }
    // Normalize to last 24 points
    history = history.slice(-24);
    while (history.length < 24) history.unshift(history[0] || 0);

    return {
      host: h,
      name: config.name,
      initials: config.initials,
      url: config.url,
      color: config.color,
      status: statusLabel,
      isUp: statusLabel === 'UP',
      isDegraded: isDegraded,
      isDown: statusLabel === 'DOWN',
      respTime: respTime,
      loadTime: loadTime,
      history: history
    };
  }).filter(s => s.host !== 'UNKNOWN');

  // Sort: DOWN first, then DEGRADED, then UP
  services.sort((a, b) => {
    const order = { 'DOWN': 0, 'DEGRADED': 1, 'UP': 2 };
    return order[a.status] - order[b.status];
  });

  const upCount = services.filter(s => s.isUp && !s.isDegraded).length;
  const degradedCount = services.filter(s => s.isDegraded).length;
  const downCount = services.filter(s => s.isDown).length;
  const totalCount = services.length;
  const allOk = downCount === 0 && degradedCount === 0;

  const now = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit' });

  // Generate sparkline bars
  const generateSparkline = (history, isDown) => {
    const maxVal = Math.max(...history, 0.001);
    return history.map((val, idx) => {
      const height = Math.max((val / maxVal) * 100, 5);
      let barClass = 'ok';
      if (isDown) {
        barClass = 'error';
      } else if (val > maxVal * 0.7) {
        barClass = 'warning';
      } else if (val > maxVal * 0.9) {
        barClass = 'error';
      }
      return `<div class="sparkline-bar ${barClass}" style="height: ${height}%" title="${(val * 1000).toFixed(0)}ms"></div>`;
    }).join('');
  };

  // Render services
  const servicesHtml = services.length > 0 ? services.map(s => {
    const cardClass = s.isDown ? 'status-down' : (s.isDegraded ? 'status-degraded' : '');
    const badgeClass = s.isDown ? 'down' : (s.isDegraded ? 'degraded' : 'up');
    const badgeText = s.isDown ? 'Fora do Ar' : (s.isDegraded ? 'Lento' : 'Operacional');

    const respClass = s.respTime > 3000 ? 'bad' : (s.respTime > 1500 ? 'warning' : 'good');
    const loadClass = s.loadTime > 5000 ? 'bad' : (s.loadTime > 2500 ? 'warning' : 'good');

    return `
      <div class="service-card ${cardClass}" onclick="window.open('https://${s.url}', '_blank')">
        <span class="service-status-badge ${badgeClass}">${badgeText}</span>
        <div class="service-header">
          <div class="service-logo gov" style="background: linear-gradient(135deg, ${s.color} 0%, ${s.color}dd 100%)">
            ${s.initials}
          </div>
          <div class="service-info">
            <h3 class="service-name">${s.name}</h3>
            <p class="service-url">${s.url}</p>
          </div>
        </div>
        <div class="sparkline-container">
          <div class="sparkline-label">
            <span>24h atr√°s</span>
            <span>Agora</span>
          </div>
          <div class="sparkline">
            ${generateSparkline(s.history, s.isDown)}
          </div>
        </div>
        <div class="service-metrics">
          <div class="metric">
            <div class="metric-value ${respClass}">${s.respTime > 0 ? s.respTime + 'ms' : '--'}</div>
            <div class="metric-label">Resposta</div>
          </div>
          <div class="metric">
            <div class="metric-value ${loadClass}">${s.loadTime > 0 ? (s.loadTime / 1000).toFixed(1) + 's' : '--'}</div>
            <div class="metric-label">Carregamento</div>
          </div>
          <div class="metric">
            <div class="metric-value ${s.isDown ? 'bad' : 'good'}">${s.isDown ? '‚ùå' : '‚úì'}</div>
            <div class="metric-label">Status</div>
          </div>
        </div>
      </div>
    `;
  }).join('') : `
    <div style="grid-column: 1/-1; text-align: center; padding: 60px; color: #6c757d;">
      <div style="font-size: 48px; margin-bottom: 16px;">üì°</div>
      <h3 style="color: #212529; margin-bottom: 8px;">Aguardando dados...</h3>
      <p>Os dados dos servi√ßos ser√£o exibidos assim que estiverem dispon√≠veis.</p>
    </div>
  `;

  htmlNode.innerHTML = styles + `
    <div class="govdetector-container">
      <header class="header">
        <div class="header-left">
          <div class="header-logo">üèõÔ∏è</div>
          <div>
            <h1 class="header-title">GovDetector RR</h1>
            <p class="header-subtitle">Monitor de Status - Sites Governamentais de Roraima</p>
          </div>
        </div>
        <div class="header-right">
          <div class="status-summary ${allOk ? 'all-ok' : 'has-issues'}">
            <span class="status-dot ${allOk ? 'green' : 'red'}"></span>
            ${allOk ? 'Todos os sistemas operacionais' : (downCount > 0 ? downCount + ' servi√ßo(s) fora do ar' : degradedCount + ' servi√ßo(s) lento(s)')}
          </div>
          <div class="update-time">Atualizado: ${now}</div>
        </div>
      </header>

      <div class="services-grid">
        ${servicesHtml}
      </div>

      <footer class="footer">
        <div class="footer-legend">
          <div class="legend-item">
            <span class="legend-dot green"></span>
            <span>Operacional (${upCount})</span>
          </div>
          <div class="legend-item">
            <span class="legend-dot yellow"></span>
            <span>Lento (${degradedCount})</span>
          </div>
          <div class="legend-item">
            <span class="legend-dot red"></span>
            <span>Fora do Ar (${downCount})</span>
          </div>
        </div>
        <div>
          Dados coletados via Zabbix ‚Ä¢ Atualiza√ß√£o autom√°tica a cada 30s
        </div>
      </footer>
    </div>
  `;
})();
