/* PAINEL 1: HEADER OTIMIZADO */
(() => {
  if (!htmlNode || !data) return;

  // Carrega configura√ß√£o (inline para evitar HTTP request extra)
  const CONFIG = {
    thresholds: {
      response_time: { good: 1000, warning: 3000, critical: 5000 },
      load_time: { good: 2000, warning: 5000, critical: 10000 }
    }
  };

  // Parser inline otimizado
  const toNumber = (v) => {
    if (v == null) return null;
    if (typeof v === 'number') return Number.isFinite(v) ? v : null;
    if (typeof v === 'string') {
      let s = v.trim().toLowerCase();
      let m = 1;
      if (s.endsWith('ms')) { s = s.replace('ms', ''); m = 0.001; }
      else if (s.endsWith('s')) s = s.replace('s', '');
      const n = parseFloat(s.replace(/[^0-9.-]/g, ''));
      return Number.isFinite(n) ? n * m : null;
    }
    return null;
  };

  const getLastValue = (frame) => {
    if (!frame?.fields) return null;
    const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
    if (!vf) return null;
    const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
    return vals.length ? vals[vals.length - 1] : null;
  };

  const getHost = (frame) => {
    const f = frame?.fields?.[1];
    const labels = f?.labels || frame.labels || {};
    return labels.host || labels.Host || 'UNKNOWN';
  };

  // Agrupa por refId
  const byRef = {};
  (data.series || []).forEach(s => {
    const ref = s.refId || 'unknown';
    (byRef[ref] = byRef[ref] || []).push(s);
  });

  // Indexa m√©tricas
  const index = (frames) => {
    const map = {};
    (frames || []).forEach(f => {
      const h = getHost(f);
      if (h !== 'UNKNOWN') map[h] = toNumber(getLastValue(f));
    });
    return map;
  };

  const ping = index(byRef['A']);
  const status = index(byRef['B']);

  // Calcula estat√≠sticas
  const hosts = new Set([...Object.keys(ping), ...Object.keys(status)]);
  let online = 0, offline = 0;

  hosts.forEach(h => {
    const p = ping[h];
    const s = status[h];
    const isUp = (p != null && p > 0) || (s != null && s >= 200 && s < 400);
    isUp ? online++ : offline++;
  });

  const total = online + offline || 1;
  const uptime = Math.round((online / total) * 100);
  const circ = 2 * Math.PI * 25;
  const offset = circ - (uptime / 100) * circ;
  const time = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit' });

  // Renderiza
  htmlNode.innerHTML = `
    <style>
      @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
      * { margin: 0; padding: 0; box-sizing: border-box; }
      .header-container { font-family: 'Inter', sans-serif; background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%); color: #fff; min-height: 100%; display: flex; flex-direction: column; }
      .navbar { display: flex; align-items: center; justify-content: space-between; padding: 15px 30px; border-bottom: 1px solid rgba(255,255,255,0.1); gap: 20px; flex-wrap: wrap; }
      .logo-section { display: flex; align-items: center; gap: 15px; }
      .logo-icon { width: 45px; height: 45px; background: linear-gradient(135deg, #e94334 0%, #ff6b5b 100%); border-radius: 10px; display: flex; align-items: center; justify-content: center; font-size: 24px; }
      .logo-text { display: flex; flex-direction: column; }
      .logo-title { font-size: 22px; font-weight: 700; }
      .logo-title span { color: #e94334; }
      .logo-subtitle { font-size: 11px; color: #8892b0; letter-spacing: 1px; text-transform: uppercase; }
      .stats-section { display: flex; align-items: center; gap: 20px; flex: 1; justify-content: center; }
      .stat-box { display: flex; flex-direction: column; align-items: center; padding: 10px 20px; border-radius: 8px; background: rgba(255,255,255,0.05); min-width: 100px; }
      .stat-value { font-size: 28px; font-weight: 700; }
      .stat-value.online { color: #4ade80; }
      .stat-value.offline { color: #ef4444; }
      .stat-label { font-size: 11px; color: #8892b0; text-transform: uppercase; letter-spacing: 1px; margin-top: 3px; }
      .uptime-section { display: flex; align-items: center; gap: 15px; }
      .uptime-ring { position: relative; width: 60px; height: 60px; }
      .uptime-ring svg { transform: rotate(-90deg); }
      .uptime-ring circle { fill: none; stroke-width: 5; }
      .uptime-ring .bg { stroke: rgba(255,255,255,0.1); }
      .uptime-ring .progress { stroke: #4ade80; stroke-linecap: round; transition: stroke-dashoffset 0.5s; }
      .uptime-text { position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%); font-size: 14px; font-weight: 600; }
      .update-info { text-align: right; }
      .update-time { font-size: 14px; color: #ccd6f6; }
      .update-label { font-size: 10px; color: #8892b0; text-transform: uppercase; }
      .live-dot { display: inline-block; width: 8px; height: 8px; background: #4ade80; border-radius: 50%; margin-right: 5px; animation: pulse 2s infinite; }
      @keyframes pulse { 0%, 100% { opacity: 1; } 50% { opacity: 0.5; } }
      @media (max-width: 768px) { .navbar { flex-direction: column; align-items: stretch; } .stats-section { justify-content: space-around; } }
    </style>
    <div class="header-container">
      <div class="navbar">
        <div class="logo-section">
          <div class="logo-icon">üì°</div>
          <div class="logo-text">
            <div class="logo-title">Gov<span>Down</span> Detector</div>
            <div class="logo-subtitle">SEFAZ Roraima ‚Ä¢ Monitoramento em Tempo Real</div>
          </div>
        </div>
        <div class="stats-section">
          <div class="stat-box"><div class="stat-value online">${online}</div><div class="stat-label">‚úì Online</div></div>
          <div class="stat-box"><div class="stat-value offline">${offline}</div><div class="stat-label">‚úó Offline</div></div>
        </div>
        <div class="uptime-section">
          <div class="uptime-ring">
            <svg width="60" height="60"><circle class="bg" cx="30" cy="30" r="25"/><circle class="progress" cx="30" cy="30" r="25" stroke-dasharray="${circ}" stroke-dashoffset="${offset}"/></svg>
            <div class="uptime-text">${uptime}%</div>
          </div>
          <div class="update-info"><div class="update-time"><span class="live-dot"></span>${time}</div><div class="update-label">√öltima atualiza√ß√£o</div></div>
        </div>
      </div>
    </div>
  `;
})();

