(() => {
  if (!htmlNode || !data) return;

  // Configura√ß√£o inline
  const SERVICES = {
    'SITE SEFAZ': { name: 'SEFAZ', url: 'sefaz.rr.gov.br', emoji: 'üí∞', priority: 'critical' },
