    'SITE IPEM': { name: 'IPEM', url: 'ipem.rr.gov.br', emoji: '‚öñÔ∏è', priority: 'low' },
    'SITE IMPRENSA OFICIAL': { name: 'IMPRENSA', url: 'imprensaoficial.rr.gov.br', emoji: 'üì∞', priority: 'high' }
  };

  // Parser otimizado
  const toNum = (v) => {
    if (v == null) return null;
    if (typeof v === 'number') return Number.isFinite(v) ? v : null;
    if (typeof v === 'string') {
      let s = v.trim().toLowerCase(), m = 1;
      if (s.endsWith('ms')) { s = s.replace('ms', ''); m = 0.001; }
      else if (s.endsWith('s')) s = s.replace('s', '');
      const n = parseFloat(s.replace(/[^0-9.-]/g, ''));
      return Number.isFinite(n) ? n * m : null;
    }
    return null;
  };

  const getLast = (f) => {
    if (!f?.fields) return null;
    const vf = f.fields.find(x => x.type === 'number') || f.fields[1];
    if (!vf) return null;
    const v = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
    return v.length ? v[v.length - 1] : null;
  };

  const getVals = (f, n = 12) => {
    if (!f?.fields) return [];
    const vf = f.fields.find(x => x.type === 'number') || f.fields[1];
    if (!vf) return [];
    const v = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
    return v.slice(-n).map(toNum).filter(x => x != null);
  };

  const getHost = (f) => {
    const field = f?.fields?.[1];
    const labels = field?.labels || f.labels || {};
    return labels.host || labels.Host || 'UNKNOWN';
  };

  // Agrupa e indexa
  const byRef = {};
  (data.series || []).forEach(s => {
    const r = s.refId || 'unknown';
    (byRef[r] = byRef[r] || []).push(s);
  });

  const idx = (frames, hist = false) => {
    const m = {};
    (frames || []).forEach(f => {
      const h = getHost(f);
      if (h !== 'UNKNOWN') m[h] = hist ? getVals(f) : toNum(getLast(f));
    });
    return m;
  };

  const ping = idx(byRef['A']);
  const status = idx(byRef['B']);
  const resp = idx(byRef['C']);
  const load = idx(byRef['D']);
  const respHist = idx(byRef['C'], true);
  const loadHist = idx(byRef['D'], true);

  // Monta servi√ßos
  const hosts = new Set([...Object.keys(ping), ...Object.keys(status), ...Object.keys(resp), ...Object.keys(load)]);
  const services = Array.from(hosts)
    .filter(h => h !== 'UNKNOWN')
    .map(h => {
      const cfg = SERVICES[h] || { name: h.replace(/^SITE\s*/i, ''), url: '', emoji: 'üèõÔ∏è', priority: 'medium' };
      const p = ping[h], s = status[h], r = resp[h], l = load[h];
      const isUp = (p != null && p > 0) || (s != null && s >= 200 && s < 400);
      const rMs = Math.round((r || 0) * 1000), lMs = Math.round((l || 0) * 1000);
      let st = 'online';
      if (!isUp && (p != null || s != null)) st = 'offline';
      else if (lMs > 5000 || rMs > 3000) st = 'degraded';
      let hist = loadHist[h] || respHist[h] || [];
      while (hist.length < 12) hist.unshift(0);
      hist = hist.slice(-12);
      return { host: h, name: cfg.name, url: cfg.url, emoji: cfg.emoji, priority: cfg.priority, status: st, resp: rMs, load: lMs, hist };
    })
    .sort((a, b) => {
      const sOrd = { offline: 0, degraded: 1, online: 2 };
      const pOrd = { critical: 0, high: 1, medium: 2, low: 3 };
      const sDiff = sOrd[a.status] - sOrd[b.status];
      return sDiff !== 0 ? sDiff : pOrd[a.priority] - pOrd[b.priority];
    });

  // Sparkline
  const spark = (hist, off) => {
    const max = Math.max(...hist, 0.001);
    return hist.map(v => {
      const h = Math.max((v / max) * 100, 10);
      const cls = off ? 'danger' : (v > max * 0.7 ? 'warning' : '');
      return `<div class="spark-bar ${cls}" style="height:${h}%"></div>`;
    }).join('');
  };

  // Renderiza cards
  const cards = services.map(s => {
    const cCls = s.status === 'offline' ? 'offline' : (s.status === 'degraded' ? 'degraded' : '');
    const sCls = `status-${s.status}`;
    const sTxt = s.status === 'offline' ? '‚úó Offline' : (s.status === 'degraded' ? '‚ö† Lento' : '‚úì Online');
    const rCls = s.resp > 3000 ? 'bad' : (s.resp > 1000 ? 'warning' : 'good');
    const lCls = s.load > 5000 ? 'bad' : (s.load > 2000 ? 'warning' : 'good');
    return `<div class="service-card ${cCls}" onclick="window.open('https://${s.url}', '_blank')">
      <div class="card-header">
        <div class="service-icon">${s.emoji}</div>
        <div class="service-info"><div class="service-name">${s.name}</div><div class="service-url">${s.url}</div></div>
