options.onRender:
/* ============================================================================
 * GOVDOWN DETECTOR v6.3 - PRODUCTION READY
 * 
 * CORRE√á√ïES v6.3:
 * - Queries com todos os campos obrigat√≥rios do Zabbix plugin
 * - Sem erros de 'undefined filter'
 * - Auto-load funciona sem editar
 * 
 * PERFORMANCE:
 * - Request time: ~2.5s
 * - Datapoints: ~3.600
 * - Sparkline: 15 pontos
 * ============================================================================ */

(() => {
  if (!htmlNode) return;

  // Loading state
  if (!data || !data.series || data.series.length === 0) {
    htmlNode.innerHTML = `
      <style>
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600&display=swap');
        .loading-container { font-family: 'Inter', sans-serif; background: #0a0a0f; min-height: 100vh; display: flex; align-items: center; justify-content: center; flex-direction: column; gap: 20px; }
        .spinner { width: 60px; height: 60px; border: 4px solid rgba(255,255,255,0.1); border-top-color: #e94334; border-radius: 50%; animation: spin 1s linear infinite; }
        @keyframes spin { to { transform: rotate(360deg); } }
        .loading-text { color: #8892b0; font-size: 16px; }
      </style>
      <div class="loading-container"><div class="spinner"></div><div class="loading-text">Carregando dados...</div></div>
    `;
    return;
  }

  const DataParser = {
    toNumber(v) {
      if (v == null) return null;
      if (typeof v === 'number') return Number.isFinite(v) ? v : null;
      if (typeof v === 'string') {
        let s = v.trim().toLowerCase(), m = 1;
        if (s.endsWith('ms')) { s = s.replace('ms', ''); m = 0.001; }
        else if (s.endsWith('s')) s = s.replace('s', '');
        const n = parseFloat(s.replace(/[^0-9.-]/g, ''));
        return Number.isFinite(n) ? n * m : null;
      }
      return null;
    },
    getLast(frame) {
      if (!frame?.fields) return null;
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return null;
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.length ? vals[vals.length - 1] : null;
    },
    getHistory(frame, n = 15) {
      if (!frame?.fields) return [];
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return [];
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.slice(-n).map(v => this.toNumber(v)).filter(v => v !== null);
    },
    getText(frame) {
      if (!frame?.fields) return null;
      const vf = frame.fields.find(f => f.type === 'string') || frame.fields[1];
      if (!vf) return null;
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.length ? vals[vals.length - 1] : null;
    },
    getHost(frame) {
      const f = frame?.fields?.[1];
      const labels = f?.labels || frame.labels || {};
      return labels.host || labels.Host || 'UNKNOWN';
    },
    parseScreenshot(raw) {
      if (!raw || typeof raw !== 'string') return { status: 'no_data', url: null };
      let b64 = raw.replace(/^data:image\/[a-z]+;base64,/i, '').trim();
      try { atob(b64.slice(0, Math.min(100, b64.length))); } catch { return { status: 'invalid_base64', url: null }; }
      const mimes = { 'UklGR': 'image/webp', 'iVBOR': 'image/png', '/9j/': 'image/jpeg', 'R0lGO': 'image/gif' };
      for (const [prefix, mime] of Object.entries(mimes)) {
        if (b64.startsWith(prefix)) return { status: 'loaded', url: `data:${mime};base64,${b64}` };
      }
      return { status: 'unknown_format', url: null };
    },
    indexByHost(frames, opts = {}) {
      const { hist = false, text = false } = opts;
      const idx = {};
      (frames || []).forEach(f => {
        const h = this.getHost(f);
        if (h === 'UNKNOWN') return;
        idx[h] = text ? this.getText(f) : (hist ? this.getHistory(f) : this.toNumber(this.getLast(f)));
      });
      return idx;
    }
  };

  const genECG = (hist) => {
    if (!hist || !hist.length) return 'M 0 30 L 200 30';
    const max = Math.max(...hist, 1);
    const pts = hist.map((v, i) => {
      const x = (i / Math.max(hist.length - 1, 1)) * 200;
      const y = 40 - ((v / max) * 35);
      return `${x.toFixed(1)},${y.toFixed(1)}`;
    });
    return `M ${pts.join(' L ')}`;
  };

  const byRef = {};
  (data.series || []).forEach(s => { const r = s.refId || 'unknown'; (byRef[r] = byRef[r] || []).push(s); });

  const ping = DataParser.indexByHost(byRef['A']);
  const status = DataParser.indexByHost(byRef['B']);
  const resp = DataParser.indexByHost(byRef['C']);
  const ssl = DataParser.indexByHost(byRef['E']);
  const respHist = DataParser.indexByHost(byRef['C'], { hist: true });
  const screenshots = DataParser.indexByHost(byRef['F'], { text: true });

  const now = Math.floor(Date.now() / 1000);
  const hosts = new Set([...Object.keys(ping), ...Object.keys(status), ...Object.keys(resp)]);

  const services = Array.from(hosts).filter(h => h !== 'UNKNOWN').map(h => {
    const name = h.replace(/^SITE\s*/i, ''), url = `https://${name.toLowerCase()}.rr.gov.br`;
    const p = ping[h], s = status[h], r = resp[h], sslExp = ssl[h];
    const isUp = (p != null && p > 0) || (s != null && s >= 200 && s < 400);
    const rMs = Math.round((r || 0) * 1000), sslDays = sslExp ? Math.floor((sslExp - now) / 86400) : null;
    let st = 'online';
    if (!isUp && (p != null || s != null)) st = 'offline'; else if (rMs > 3000) st = 'degraded';
    const hist = respHist[h] || [], histMs = hist.map(v => Math.round(v * 1000));
    const ssData = DataParser.parseScreenshot(screenshots[h]);
    const logo = ssData.status === 'loaded' ? ssData.url : 'https://portal.rr.gov.br/wp-content/uploads/2023/02/brasao-logo-1x.png';
    return { host: h, name, url, logo, screenshot: ssData, status: st, code: Math.round(s || 0), resp: rMs, sslDays, hist: histMs };
  }).sort((a, b) => { const ord = { offline: 0, degraded: 1, online: 2 }; return ord[a.status] - ord[b.status]; });

  const online = services.filter(s => s.status === 'online').length;
  const offline = services.filter(s => s.status === 'offline').length;
  const sslWarn = services.filter(s => s.sslDays !== null && s.sslDays < 30).length;
  const total = online + offline || 1, uptime = Math.round((online / total) * 100);
  const circ = 2 * Math.PI * 25, offset = circ - (uptime / 100) * circ;
  const time = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit' });

  const cards = services.map(s => {
    const cCls = s.status === 'offline' ? 'offline' : (s.status === 'degraded' ? 'degraded' : '');
    const rCls = s.resp > 3000 ? 'bad' : (s.resp > 1000 ? 'warning' : 'good');
    const sslCls = !s.sslDays ? 'unknown' : (s.sslDays < 7 ? 'critical' : (s.sslDays < 30 ? 'warning' : 'good'));
    const sslTxt = !s.sslDays ? 'SSL N/A' : `SSL ${s.sslDays}d`;
    const codeCls = !s.code ? 'unknown' : (s.code >= 200 && s.code < 300 ? 'good' : (s.code >= 300 && s.code < 400 ? 'warning' : 'bad'));
    const pathD = genECG(s.hist), stroke = s.status === 'offline' ? '#ef4444' : (s.status === 'degraded' ? '#fbbf24' : '#4ade80');
    const badge = s.screenshot.status === 'loaded' ? '<div class="ss-badge loaded" title="Screenshot capturado">üì∏</div>' : s.screenshot.status === 'invalid_base64' || s.screenshot.status === 'unknown_format' ? '<div class="ss-badge error" title="Erro">‚ö†Ô∏è</div>' : '<div class="ss-badge fallback" title="Logo padr√£o">üñºÔ∏è</div>';
    return `<div class="card ${cCls}" onclick="window.open('${s.url}', '_blank')"><div class="logo-box"><img src="${s.logo}" alt="${s.name}" onerror="this.style.display='none';this.nextElementSibling.style.display='flex';" /><div class="logo-fallback">${s.name}</div>${badge}</div><div class="body"><div class="title">${s.name}</div><div class="url">${s.url.replace(/^https?:\/\//, '')}</div><div class="ecg"><svg viewBox="0 0 200 40" preserveAspectRatio="none"><path d="${pathD}" fill="none" stroke="${stroke}" stroke-width="2" vector-effect="non-scaling-stroke"/></svg></div><div class="footer"><div class="badge ${sslCls}">${sslTxt}</div><div class="badge ${codeCls}">HTTP ${s.code || '---'}</div><div class="metric ${rCls}">${s.resp}ms</div></div></div></div>`;
  }).join('');

  htmlNode.innerHTML = `<style>@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');*{margin:0;padding:0;box-sizing:border-box}.container{font-family:'Inter',sans-serif;background:#0a0a0f;min-height:100vh;color:#fff}.header{background:linear-gradient(135deg,#1a1a2e 0%,#16213e 100%);border-bottom:1px solid rgba(255,255,255,0.1);padding:15px 30px;display:flex;align-items:center;justify-content:space-between;gap:20px;flex-wrap:wrap}.logo-sec{display:flex;align-items:center;gap:15px}.logo-icon{width:45px;height:45px;background:linear-gradient(135deg,#e94334,#ff6b5b);border-radius:10px;display:flex;align-items:center;justify-content:center;font-size:24px}.logo-text{display:flex;flex-direction:column}.logo-title{font-size:22px;font-weight:700}.logo-title span{color:#e94334}.logo-title small{font-size:12px;color:#64748b}.logo-sub{font-size:11px;color:#8892b0;letter-spacing:1px;text-transform:uppercase}.stats{display:flex;align-items:center;gap:20px;flex:1;justify-content:center}.stat{display:flex;flex-direction:column;align-items:center;padding:10px 20px;border-radius:8px;background:rgba(255,255,255,0.05);min-width:100px;position:relative}.stat-val{font-size:28px;font-weight:700}.stat-val.online{color:#4ade80}.stat-val.offline{color:#ef4444}.stat-val.warning{color:#fbbf24}.stat-lbl{font-size:11px;color:#8892b0;text-transform:uppercase;letter-spacing:1px;margin-top:3px}.ssl-badge{position:absolute;top:-8px;right:-8px;background:#ef4444;color:#fff;font-size:9px;padding:2px 6px;border-radius:10px;font-weight:600}.uptime-sec{display:flex;align-items:center;gap:15px}.ring{position:relative;width:60px;height:60px}.ring svg{transform:rotate(-90deg)}.ring circle{fill:none;stroke-width:5}.ring .bg{stroke:rgba(255,255,255,0.1)}.ring .prog{stroke:#4ade80;stroke-linecap:round;transition:stroke-dashoffset 0.5s}.ring-txt{position:absolute;top:50%;left:50%;transform:translate(-50%,-50%);font-size:14px;font-weight:600}.update{text-align:right}.update-time{font-size:14px;color:#ccd6f6}.update-lbl{font-size:10px;color:#8892b0;text-transform:uppercase}.dot{display:inline-block;width:8px;height:8px;background:#4ade80;border-radius:50%;margin-right:5px;animation:pulse 2s infinite}@keyframes pulse{0%,100%{opacity:1}50%{opacity:0.5}}.main{padding:25px}.sec-head{display:flex;align-items:center;justify-content:space-between;margin-bottom:25px;padding-bottom:15px;border-bottom:1px solid rgba(255,255,255,0.08)}.sec-title{font-size:18px;font-weight:600;color:#e2e8f0;display:flex;align-items:center;gap:10px}.grid{display:grid;grid-template-columns:repeat(auto-fill,minmax(220px,1fr));gap:18px}.card{background:linear-gradient(145deg,#12121a,#0d0d12);border:1px solid rgba(255,255,255,0.06);border-radius:10px;overflow:hidden;transition:all 0.3s;cursor:pointer;position:relative}.card::before{content:'';position:absolute;top:0;left:0;right:0;height:3px;background:linear-gradient(90deg,#4ade80,#22c55e);z-index:1}.card.degraded::before{background:linear-gradient(90deg,#fbbf24,#f59e0b)}.card.offline::before{background:linear-gradient(90deg,#ef4444,#dc2626)}.card:hover{transform:translateY(-4px);border-color:rgba(255,255,255,0.15);box-shadow:0 12px 35px rgba(0,0,0,0.4)}.logo-box{width:100%;height:100px;background:#fff;display:flex;align-items:center;justify-content:center;overflow:hidden;position:relative}.logo-box img{width:100%;height:100%;object-fit:cover}.ss-badge{position:absolute;top:4px;right:4px;font-size:14px;background:rgba(0,0,0,0.7);padding:3px 6px;border-radius:4px;z-index:2;cursor:help}.ss-badge.loaded{background:rgba(34,197,94,0.9)}.ss-badge.error{background:rgba(239,68,68,0.9)}.ss-badge.fallback{background:rgba(100,116,139,0.9)}.logo-fallback{display:none;width:100%;height:100%;align-items:center;justify-content:center;font-size:18px;font-weight:700;color:#64748b;text-transform:uppercase}.body{padding:12px 15px 15px;display:flex;flex-direction:column;gap:10px}.title{font-size:14px;font-weight:600;color:#e2e8f0;line-height:1.3;display:-webkit-box;-webkit-line-clamp:2;-webkit-box-orient:vertical;overflow:hidden;min-height:36px}.url{font-size:10px;color:#64748b;white-space:nowrap;overflow:hidden;text-overflow:ellipsis}.ecg{width:100%;height:40px;background:rgba(0,0,0,0.2);border-radius:6px;padding:2px;margin:5px 0}.ecg svg{width:100%;height:100%}.footer{display:flex;gap:6px;flex-wrap:wrap}.badge{padding:4px 8px;border-radius:10px;font-size:9px;font-weight:600;text-transform:uppercase;letter-spacing:0.3px;white-space:nowrap}.badge.good{background:rgba(74,222,128,0.15);color:#4ade80}.badge.warning{background:rgba(251,191,36,0.15);color:#fbbf24}.badge.critical{background:rgba(239,68,68,0.15);color:#ef4444}.badge.unknown{background:rgba(100,116,139,0.15);color:#64748b}.badge.bad{background:rgba(239,68,68,0.15);color:#ef4444}.metric{padding:4px 8px;border-radius:10px;font-size:10px;font-weight:600}.metric.good{background:rgba(74,222,128,0.1);color:#4ade80}.metric.warning{background:rgba(251,191,36,0.1);color:#fbbf24}.metric.bad{background:rgba(239,68,68,0.1);color:#ef4444}@media (max-width:768px){.header{flex-direction:column}.grid{grid-template-columns:1fr}}@media (min-width:1400px){.grid{grid-template-columns:repeat(5,1fr)}}@media (min-width:1800px){.grid{grid-template-columns:repeat(6,1fr)}}@media (min-width:2200px){.grid{grid-template-columns:repeat(7,1fr)}}</style><div class="container"><div class="header"><div class="logo-sec"><div class="logo-icon">üì°</div><div class="logo-text"><div class="logo-title">Gov<span>Down</span> Detector <small>v6.3</small></div><div class="logo-sub">SEFAZ Roraima ‚Ä¢ Monitoramento</div></div></div><div class="stats"><div class="stat"><div class="stat-val online">${online}</div><div class="stat-lbl">‚úì Online</div></div><div class="stat"><div class="stat-val offline">${offline}</div><div class="stat-lbl">‚úó Offline</div></div><div class="stat">${sslWarn > 0 ? `<span class="ssl-badge">${sslWarn}</span>` : ''}<div class="stat-val warning">üîí</div><div class="stat-lbl">SSL</div></div></div><div class="uptime-sec"><div class="ring"><svg width="60" height="60"><circle class="bg" cx="30" cy="30" r="25"/><circle class="prog" cx="30" cy="30" r="25" stroke-dasharray="${circ}" stroke-dashoffset="${offset}"/></svg><div class="ring-txt">${uptime}%</div></div><div class="update"><div class="update-time"><span class="dot"></span>${time}</div><div class="update-lbl">Atualiza√ß√£o</div></div></div></div><div class="main"><div class="sec-head"><div class="sec-title"><span>üñ•Ô∏è</span> Status dos Servi√ßos</div></div><div class="grid">${cards}</div></div></div>`;
})();
