/* SEFAZ Infrastructure Analytics v2.0 - Analytical View */
(() => {
  if (!htmlNode || !data?.series?.length) {
    htmlNode.innerHTML = '<div style="font-family:Inter;background:#0a0a0f;min-height:100vh;display:flex;align-items:center;justify-content:center;color:#8892b0;">Carregando infraestrutura...</div>';
    return;
  }

  const DataParser = {
    toNumber(v) {
      if (v == null) return null;
      if (typeof v === 'number') return Number.isFinite(v) ? v : null;
      if (typeof v === 'string') {
        const n = parseFloat(v.trim().replace(/[^0-9.-]/g, ''));
        return Number.isFinite(n) ? n : null;
      }
      return null;
    },
    getLast(frame) {
      if (!frame?.fields) return null;
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return null;
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.length ? vals[vals.length - 1] : null;
    },
    getHistory(frame, n = 20) {
      if (!frame?.fields) return [];
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return [];
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.slice(-n).map(v => this.toNumber(v)).filter(v => v !== null);
    },
    getHost(frame) {
      const f = frame?.fields?.[1];
      const labels = f?.labels || frame.labels || {};
      return labels.host || labels.Host || 'UNKNOWN';
    },
    indexByHost(frames, opts = {}) {
      const { hist = false } = opts, idx = {};
      (frames || []).forEach(f => {
        const h = this.getHost(f);
        if (h === 'UNKNOWN') return;
        idx[h] = hist ? this.getHistory(f) : this.toNumber(this.getLast(f));
      });
      return idx;
    }
  };

  const VizHelpers = {
    genSparkline(hist) {
      if (!hist || !hist.length) return 'M 0 20 L 100 20';
      const max = Math.max(...hist, 1), min = Math.min(...hist, 0), range = max - min || 1;
      const pts = hist.map((v, i) => {
        const x = (i / Math.max(hist.length - 1, 1)) * 100;
        const y = 30 - ((v - min) / range) * 25;
        return `${x.toFixed(1)},${y.toFixed(1)}`;
      });
      return `M ${pts.join(' L ')}`;
    },
    formatBytes(bytes) {
      if (bytes == null || bytes === 0) return '0 B';
      const k = 1024, sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
      const i = Math.floor(Math.log(Math.abs(bytes)) / Math.log(k));
      return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + ' ' + sizes[i];
    },
    formatUptime(seconds) {
      if (!seconds) return '0s';
      const d = Math.floor(seconds / 86400), h = Math.floor((seconds % 86400) / 3600);
      if (d > 0) return `${d}d ${h}h`;
      const m = Math.floor((seconds % 3600) / 60);
      return h > 0 ? `${h}h ${m}m` : `${m}m`;
    },
    getStatusClass(value, thresholds) {
      if (value == null) return 'unknown';
      if (value >= thresholds.critical) return 'critical';
      if (value >= thresholds.warning) return 'warning';
      return 'good';
    }
  };

  const byRef = {};
  (data.series || []).forEach(s => { const r = s.refId || 'unknown'; (byRef[r] = byRef[r] || []).push(s); });

  const cpuUtil = DataParser.indexByHost(byRef['A']);
  const cpuUtilHist = DataParser.indexByHost(byRef['A'], { hist: true });
  const memPAvail = DataParser.indexByHost(byRef['B']);
  const memTotal = DataParser.indexByHost(byRef['C']);
  const memAvail = DataParser.indexByHost(byRef['D']);
  const memAvailHist = DataParser.indexByHost(byRef['D'], { hist: true });
  const diskPFree = DataParser.indexByHost(byRef['E']);
  const netIn = DataParser.indexByHost(byRef['F']);
  const netOut = DataParser.indexByHost(byRef['G']);
  const uptime = DataParser.indexByHost(byRef['H']);
  const agentPing = DataParser.indexByHost(byRef['I']);
  const triggers = DataParser.indexByHost(byRef['J']);

  const hostnames = new Set([...Object.keys(cpuUtil), ...Object.keys(memPAvail), ...Object.keys(uptime)]);

  const hosts = Array.from(hostnames).filter(h => h !== 'UNKNOWN').map(h => {
    const cpu = cpuUtil[h], memPA = memPAvail[h], memUsedPct = memPA != null ? 100 - memPA : null;
    const diskFree = diskPFree[h], diskUsed = diskFree != null ? 100 - diskFree : null;
    const up = uptime[h], ping = agentPing[h], triggerCount = triggers[h] || 0;
    let status = 'online';
    if (ping !== 1) status = 'offline';
    else if (cpu >= 90 || memUsedPct >= 90 || diskUsed >= 90) status = 'critical';
    else if (cpu >= 75 || memUsedPct >= 75 || diskUsed >= 75) status = 'warning';
    return {
      name: h, status, cpu: cpu || 0, cpuHist: (cpuUtilHist[h] || []).map(v => 100 - v),
      memUsedPct: memUsedPct || 0, memTotal: memTotal[h] || 0, memAvail: memAvail[h] || 0,
      memAvailHist: memAvailHist[h] || [], diskUsedPct: diskUsed || 0,
      netInBps: netIn[h] || 0, netOutBps: netOut[h] || 0,
      uptime: up || 0, ping: ping === 1, triggers: Math.round(triggerCount)
    };
  }).sort((a, b) => { const ord = { offline: 0, critical: 1, warning: 2, online: 3 }; return ord[a.status] - ord[b.status]; });

  const online = hosts.filter(h => h.status === 'online').length;
  const warning = hosts.filter(h => h.status === 'warning').length;
  const critical = hosts.filter(h => h.status === 'critical').length;
  const offline = hosts.filter(h => h.status === 'offline').length;
  const totalTriggers = hosts.reduce((sum, h) => sum + h.triggers, 0);
  const avgCpu = hosts.reduce((sum, h) => sum + h.cpu, 0) / (hosts.length || 1);
  const avgMem = hosts.reduce((sum, h) => sum + h.memUsedPct, 0) / (hosts.length || 1);
  const avgDisk = hosts.reduce((sum, h) => sum + h.diskUsedPct, 0) / (hosts.length || 1);
  const time = new Date().toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit', second: '2-digit' });

  const hostCards = hosts.map(h => {
    const cpuCls = VizHelpers.getStatusClass(h.cpu, { warning: 75, critical: 90 });
    const memCls = VizHelpers.getStatusClass(h.memUsedPct, { warning: 75, critical: 90 });
    const diskCls = VizHelpers.getStatusClass(h.diskUsedPct, { warning: 80, critical: 90 });
    const cpuPath = VizHelpers.genSparkline(h.cpuHist);
    const memPath = VizHelpers.genSparkline(h.memAvailHist.map(v => 100 - v));
    const cpuStroke = h.cpu >= 90 ? '#f97316' : (h.cpu >= 75 ? '#fbbf24' : '#3b82f6');
    const memStroke = h.memUsedPct >= 90 ? '#f97316' : (h.memUsedPct >= 75 ? '#fbbf24' : '#a855f7');
    const uptimeStr = VizHelpers.formatUptime(h.uptime);
    const uptimeDays = Math.floor(h.uptime / 86400);
    const cpuHistSorted = [...h.cpuHist].sort((a, b) => a - b);
    const cpuP50 = cpuHistSorted[Math.floor(cpuHistSorted.length * 0.5)] || 0;
    const cpuP95 = cpuHistSorted[Math.floor(cpuHistSorted.length * 0.95)] || 0;
    const cpuMax = Math.max(...h.cpuHist, 0);
    const netTotalBps = h.netInBps + h.netOutBps;
    const netTotalMbps = (netTotalBps / 1024 / 1024 * 8).toFixed(1);
    return `<div class="card ${h.status}"><div class="hdr"><div class="t-sec"><div class="icon">üñ•Ô∏è</div><div><h2>${h.name}</h2><div class="meta"><span>‚è± ${uptimeStr} (${uptimeDays}d)</span><span class="s-${h.status}">üì° ${h.status.toUpperCase()}</span>${h.triggers > 0 ? `<span class="alert">üö® ${h.triggers} alerts</span>` : ''}</div></div></div><div class="sum"><div><div class="lbl">CPU</div><div class="val ${cpuCls}">${Math.round(h.cpu)}%</div></div><div><div class="lbl">RAM</div><div class="val ${memCls}">${Math.round(h.memUsedPct)}%</div></div><div><div class="lbl">Disk</div><div class="val ${diskCls}">${Math.round(h.diskUsedPct)}%</div></div></div></div><div class="grid"><div class="sec"><div class="sh"><span>‚ö°</span><span>CPU Analysis</span></div><div class="chart"><svg viewBox="0 0 200 80" preserveAspectRatio="none"><defs><linearGradient id="cpuG${h.name.replace(/\s/g, '')}" x1="0%" y1="0%" x2="0%" y2="100%"><stop offset="0%" style="stop-color:${cpuStroke};stop-opacity:0.3"/><stop offset="100%" style="stop-color:${cpuStroke};stop-opacity:0"/></linearGradient></defs><path d="${cpuPath} L 200 80 L 0 80 Z" fill="url(#cpuG${h.name.replace(/\s/g, '')})"/><path d="${cpuPath}" fill="none" stroke="${cpuStroke}" stroke-width="3" vector-effect="non-scaling-stroke"/></svg></div><div class="sr"><div class="si"><div class="sl">Current</div><div class="sv ${cpuCls}">${Math.round(h.cpu)}%</div></div><div class="si"><div class="sl">P50</div><div class="sv">${Math.round(cpuP50)}%</div></div><div class="si"><div class="sl">P95</div><div class="sv">${Math.round(cpuP95)}%</div></div><div class="si"><div class="sl">Peak</div><div class="sv">${Math.round(cpuMax)}%</div></div></div></div><div class="sec"><div class="sh"><span>üíæ</span><span>Memory Analysis</span></div><div class="chart"><svg viewBox="0 0 200 80" preserveAspectRatio="none"><defs><linearGradient id="memG${h.name.replace(/\s/g, '')}" x1="0%" y1="0%" x2="0%" y2="100%"><stop offset="0%" style="stop-color:${memStroke};stop-opacity:0.3"/><stop offset="100%" style="stop-color:${memStroke};stop-opacity:0"/></linearGradient></defs><path d="${memPath} L 200 80 L 0 80 Z" fill="url(#memG${h.name.replace(/\s/g, '')})"/><path d="${memPath}" fill="none" stroke="${memStroke}" stroke-width="3" vector-effect="non-scaling-stroke"/></svg></div><div class="sr"><div class="si"><div class="sl">Used</div><div class="sv ${memCls}">${VizHelpers.formatBytes(h.memTotal - h.memAvail)}</div></div><div class="si"><div class="sl">Available</div><div class="sv">${VizHelpers.formatBytes(h.memAvail)}</div></div><div class="si"><div class="sl">Total</div><div class="sv">${VizHelpers.formatBytes(h.memTotal)}</div></div><div class="si"><div class="sl">Usage</div><div class="sv ${memCls}">${Math.round(h.memUsedPct)}%</div></div></div></div><div class="sec"><div class="sh"><span>üíø</span><span>Storage</span></div><div class="dg"><svg viewBox="0 0 120 120"><circle cx="60" cy="60" r="50" fill="none" stroke="rgba(255,255,255,0.1)" stroke-width="12"/><circle cx="60" cy="60" r="50" fill="none" stroke="${h.diskUsedPct >= 90 ? '#f97316' : (h.diskUsedPct >= 80 ? '#fbbf24' : '#4ade80')}" stroke-width="12" stroke-dasharray="${2 * Math.PI * 50}" stroke-dashoffset="${2 * Math.PI * 50 * (1 - h.diskUsedPct / 100)}" stroke-linecap="round" transform="rotate(-90 60 60)"/><text x="60" y="55" text-anchor="middle" fill="#e2e8f0" font-size="24" font-weight="bold">${Math.round(h.diskUsedPct)}%</text><text x="60" y="75" text-anchor="middle" fill="#64748b" font-size="10">DISK USED</text></svg></div><div class="sh" style="margin-top:20px"><span>üìä</span><span>Network</span></div><div class="ns"><div class="n"><div class="nd">‚Üì INBOUND</div><div class="nv">${VizHelpers.formatBytes(h.netInBps)}/s</div></div><div class="n"><div class="nd">‚Üë OUTBOUND</div><div class="nv">${VizHelpers.formatBytes(h.netOutBps)}/s</div></div><div class="n t"><div class="nd">‚ö° TOTAL</div><div class="nv">${netTotalMbps} Mbps</div></div></div></div></div></div>`;
  }).join('');

  htmlNode.innerHTML = `<style>@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');*{margin:0;padding:0;box-sizing:border-box}.c{font-family:Inter,sans-serif;background:#0a0a0f;min-height:100vh;color:#fff;padding:20px}.h{background:linear-gradient(135deg,#1a1a2e,#16213e);border:1px solid rgba(255,255,255,.1);border-radius:12px;padding:20px 30px;margin-bottom:25px;display:flex;align-items:center;justify-content:space-between;flex-wrap:wrap;gap:20px}.h-l{display:flex;align-items:center;gap:15px}.h-i{width:50px;height:50px;background:linear-gradient(135deg,#3b82f6,#2563eb);border-radius:12px;display:flex;align-items:center;justify-content:center;font-size:26px}.h-t .t{font-size:24px;font-weight:700;color:#e2e8f0}.h-t .t span{color:#3b82f6}.h-t .st{font-size:11px;color:#8892b0;text-transform:uppercase;letter-spacing:1px;margin-top:2px}.h-s{display:flex;gap:20px;flex:1;justify-content:center}.sb{background:rgba(255,255,255,.05);border-radius:8px;padding:12px 20px;min-width:100px;text-align:center}.sb .v{font-size:28px;font-weight:700}.sb.on .v{color:#4ade80}.sb.wa .v{color:#fbbf24}.sb.cr .v{color:#f97316}.sb.of .v{color:#ef4444}.sb.tr .v{color:#a855f7}.sb .l{font-size:10px;color:#8892b0;text-transform:uppercase;letter-spacing:1px;margin-top:4px}.h-r{text-align:right}.ut{font-size:14px;color:#ccd6f6;display:flex;align-items:center;gap:8px}.pd{width:8px;height:8px;background:#4ade80;border-radius:50%;animation:p 2s infinite}@keyframes p{0%,100%{opacity:1}50%{opacity:.5}}.ul{font-size:10px;color:#64748b;text-transform:uppercase;margin-top:2px}.sm{display:grid;grid-template-columns:repeat(3,1fr);gap:15px;margin-bottom:25px}.sc{background:linear-gradient(145deg,#12121a,#0d0d12);border:1px solid rgba(255,255,255,.08);border-radius:10px;padding:20px}.sc .ct{font-size:12px;color:#8892b0;text-transform:uppercase;letter-spacing:1px;margin-bottom:12px;display:flex;align-items:center;gap:8px}.sc .cv{font-size:36px;font-weight:700;color:#e2e8f0}.sc .cl{font-size:11px;color:#64748b;margin-top:4px}.card{background:linear-gradient(145deg,#12121a,#0d0d12);border:1px solid rgba(255,255,255,.08);border-radius:12px;padding:0;margin-bottom:25px;transition:all .3s;position:relative;overflow:hidden}.card::before{content:'';position:absolute;top:0;left:0;right:0;height:4px;z-index:1}.card.online::before{background:linear-gradient(90deg,#4ade80,#22c55e)}.card.warning::before{background:linear-gradient(90deg,#fbbf24,#f59e0b)}.card.critical::before{background:linear-gradient(90deg,#f97316,#ea580c)}.card.offline::before{background:linear-gradient(90deg,#ef4444,#dc2626)}.card:hover{border-color:rgba(255,255,255,.15);box-shadow:0 12px 40px rgba(0,0,0,.5)}.hdr{display:flex;justify-content:space-between;align-items:center;padding:18px 24px;border-bottom:1px solid rgba(255,255,255,.05);background:rgba(0,0,0,.2)}.t-sec{display:flex;align-items:center;gap:16px;flex:1}.icon{width:48px;height:48px;background:linear-gradient(135deg,rgba(59,130,246,.2),rgba(37,99,235,.1));border:2px solid rgba(59,130,246,.3);border-radius:10px;display:flex;align-items:center;justify-content:center;font-size:26px;flex-shrink:0}h2{font-size:20px;font-weight:700;color:#e2e8f0;margin:0 0 6px 0}.meta{display:flex;gap:20px;flex-wrap:wrap}.meta span{font-size:12px;color:#8892b0}.s-online{color:#4ade80}.s-warning{color:#fbbf24}.s-critical{color:#f97316}.s-offline{color:#ef4444}.alert{color:#f97316!important}.sum{display:flex;gap:24px}.sum>div{text-align:center}.lbl{font-size:10px;color:#64748b;text-transform:uppercase;letter-spacing:1px;margin-bottom:6px}.val{font-size:26px;font-weight:700;color:#e2e8f0}.grid{display:grid;grid-template-columns:repeat(3,1fr);gap:0;border-top:1px solid rgba(255,255,255,.05)}.sec{padding:18px;border-right:1px solid rgba(255,255,255,.05)}.sec:last-child{border-right:none}.sh{display:flex;align-items:center;gap:8px;margin-bottom:14px}.sh span:first-child{font-size:18px}.sh span:last-child{font-size:12px;font-weight:600