/* ============================================================================
 * SEFAZ INFRASTRUCTURE MONITOR v1.1 - ANALYTICAL VIEW
 * 
 * ARQUITETURA:
 * - 10 queries consolidadas (CPU breakdown, RAM detailed, Disk, Network, System)
 * - Parsing modular e reutiliz√°vel
 * - An√°lise comparativa e trending
 * - Performance target: < 1.5s response time
 * 
 * DESIGN:
 * - Vis√£o anal√≠tica detalhada
 * - Layout: Summary header + Large analytical cards per host
 * - M√©tricas detalhadas com breakdown e hist√≥rico
 * - Ideal para an√°lise t√©cnica profunda
 * ============================================================================ */

(() => {
  if (!htmlNode) return;

  // Loading state
  if (!data || !data.series || data.series.length === 0) {
    htmlNode.innerHTML = `
      <style>
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600&display=swap');
        .loading-container { font-family: 'Inter', sans-serif; background: #0a0a0f; min-height: 100vh; display: flex; align-items: center; justify-content: center; flex-direction: column; gap: 20px; }
        .spinner { width: 60px; height: 60px; border: 4px solid rgba(255,255,255,0.1); border-top-color: #3b82f6; border-radius: 50%; animation: spin 1s linear infinite; }
        @keyframes spin { to { transform: rotate(360deg); } }
        .loading-text { color: #8892b0; font-size: 16px; }
      </style>
      <div class="loading-container"><div class="spinner"></div><div class="loading-text">Carregando infraestrutura...</div></div>
    `;
    return;
  }

  // ========== DATA PARSER MODULE ==========
  const DataParser = {
    toNumber(v) {
      if (v == null) return null;
      if (typeof v === 'number') return Number.isFinite(v) ? v : null;
      if (typeof v === 'string') {
        const s = v.trim().toLowerCase();
        const n = parseFloat(s.replace(/[^0-9.-]/g, ''));
        return Number.isFinite(n) ? n : null;
      }
      return null;
    },

    getLast(frame) {
      if (!frame?.fields) return null;
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return null;
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.length ? vals[vals.length - 1] : null;
    },

    getHistory(frame, n = 20) {
      if (!frame?.fields) return [];
      const vf = frame.fields.find(f => f.type === 'number') || frame.fields[1];
      if (!vf) return [];
      const vals = Array.isArray(vf.values) ? vf.values : (vf.values?.toArray?.() || []);
      return vals.slice(-n).map(v => this.toNumber(v)).filter(v => v !== null);
    },

    getHost(frame) {
      const f = frame?.fields?.[1];
      const labels = f?.labels || frame.labels || {};
      return labels.host || labels.Host || 'UNKNOWN';
    },

    getItemName(frame) {
      const f = frame?.fields?.[1];
      const labels = f?.labels || frame.labels || {};
      return labels.item || labels.Item || labels.name || '';
    },

    indexByHost(frames, opts = {}) {
      const { hist = false, byItem = false } = opts;
      const idx = {};

      (frames || []).forEach(f => {
        const h = this.getHost(f);
        if (h === 'UNKNOWN') return;

        if (byItem) {
          const item = this.getItemName(f);
          if (!idx[h]) idx[h] = {};
          idx[h][item] = hist ? this.getHistory(f) : this.toNumber(this.getLast(f));
        } else {
          idx[h] = hist ? this.getHistory(f) : this.toNumber(this.getLast(f));
        }
      });

      return idx;
    }
  };

  // ========== VISUALIZATION HELPERS ==========
  const VizHelpers = {
    genSparkline(hist, color = '#3b82f6') {
      if (!hist || !hist.length) return 'M 0 20 L 100 20';
      const max = Math.max(...hist, 1);
      const min = Math.min(...hist, 0);
      const range = max - min || 1;

      const pts = hist.map((v, i) => {
        const x = (i / Math.max(hist.length - 1, 1)) * 100;
        const y = 30 - ((v - min) / range) * 25;
        return `${x.toFixed(1)},${y.toFixed(1)}`;
      });

      return `M ${pts.join(' L ')}`;
    },

    formatBytes(bytes) {
      if (bytes == null || bytes === 0) return '0 B';
      const k = 1024;
      const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
      const i = Math.floor(Math.log(Math.abs(bytes)) / Math.log(k));
      return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + ' ' + sizes[i];
    },

    formatUptime(seconds) {
      if (!seconds) return '0s';
      const d = Math.floor(seconds / 86400);
      const h = Math.floor((seconds % 86400) / 3600);
      if (d > 0) return `${d}d ${h}h`;
      const m = Math.floor((seconds % 3600) / 60);
      if (h > 0) return `${h}h ${m}m`;
      return `${m}m`;
    },

    getStatusClass(value, thresholds) {
      if (value == null) return 'unknown';
      if (value >= thresholds.critical) return 'critical';
      if (value >= thresholds.warning) return 'warning';
      return 'good';
    }
  };

  // ========== DATA AGGREGATION ==========
  const byRef = {};
  (data.series || []).forEach(s => {
    const r = s.refId || 'unknown';
    (byRef[r] = byRef[r] || []).push(s);
  });

  // Query A: CPU metrics
  const cpuUtil = DataParser.indexByHost(byRef['A']);
  const cpuUtilHist = DataParser.indexByHost(byRef['A'], { hist: true });

  // Query B: Memory metrics
  const memPAvail = DataParser.indexByHost(byRef['B']);
  const memTotal = DataParser.indexByHost(byRef['C']);
  const memAvail = DataParser.indexByHost(byRef['D']);
  const memAvailHist = DataParser.indexByHost(byRef['D'], { hist: true });

  // Query E: Disk space (root partition)
  const diskPUsed = DataParser.indexByHost(byRef['E']);

  // Query F: Network (aggregated from all interfaces)
  const netIn = DataParser.indexByHost(byRef['F']);
  const netOut = DataParser.indexByHost(byRef['G']);

  // Query H: System info
  const uptime = DataParser.indexByHost(byRef['H']);
  const agentPing = DataParser.indexByHost(byRef['I']);

  // Query J: Active triggers count
  const triggers = DataParser.indexByHost(byRef['J']);

  // ========== HOST DATA CONSOLIDATION ==========
  const hostnames = new Set([
    ...Object.keys(cpuUtil),
    ...Object.keys(memPAvail),
    ...Object.keys(uptime)
  ]);

  const hosts = Array.from(hostnames)
    .filter(h => h !== 'UNKNOWN')
    .map(h => {
      const cpu = cpuUtil[h];
      const memPA = memPAvail[h];
      const memUsedPct = memPA != null ? 100 - memPA : null;
      const diskUsed = diskPUsed[h];
      const up = uptime[h];
      const ping = agentPing[h];
      const triggerCount = triggers[h] || 0;

      // Status logic
      let status = 'online';
      if (ping !== 1) status = 'offline';
      else if (cpu >= 90 || memUsedPct >= 90 || diskUsed >= 90) status = 'critical';
      else if (cpu >= 75 || memUsedPct >= 75 || diskUsed >= 75) status = 'warning';

      return {
        name: h,
        status,
        cpu: cpu || 0,
        cpuHist: cpuUtilHist[h] || [],
        memUsedPct: memUsedPct || 0,
        memTotal: memTotal[h] || 0,
        memAvail: memAvail[h] || 0,
        memAvailHist: memAvailHist[h] || [],
        diskUsedPct: diskUsed || 0,
        netInBps: netIn[h] || 0,
        netOutBps: netOut[h] || 0,
        uptime: up || 0,
        ping: ping === 1,
        triggers: Math.round(triggerCount)
      };
    })
    .sort((a, b) => {
      const ord = { offline: 0, critical: 1, warning: 2, online: 3 };
      return ord[a.status] - ord[b.status];
    });

  // ========== SUMMARY STATS ==========
  const online = hosts.filter(h => h.status === 'online').length;
  const warning = hosts.filter(h => h.status === 'warning').length;
  const critical = hosts.filter(h => h.status === 'critical').length;
  const offline = hosts.filter(h => h.status === 'offline').length;
  const totalTriggers = hosts.reduce((sum, h) => sum + h.triggers, 0);

  const avgCpu = hosts.reduce((sum, h) => sum + h.cpu, 0) / (hosts.length || 1);
  const avgMem = hosts.reduce((sum, h) => sum + h.memUsedPct, 0) / (hosts.length || 1);
  const avgDisk = hosts.reduce((sum, h) => sum + h.diskUsedPct, 0) / (hosts.length || 1);

  const time = new Date().toLocaleTimeString('pt-BR', { 
    hour: '2-digit', 
    minute: '2-digit', 
    second: '2-digit' 
  });

  // ========== HOST CARDS HTML ==========
  const hostCards = hosts.map(h => {
    const statusCls = h.status;
    const cpuCls = VizHelpers.getStatusClass(h.cpu, { warning: 75, critical: 90 });
    const memCls = VizHelpers.getStatusClass(h.memUsedPct, { warning: 75, critical: 90 });
    const diskCls = VizHelpers.getStatusClass(h.diskUsedPct, { warning: 80, critical: 90 });

    const cpuPath = VizHelpers.genSparkline(h.cpuHist, '#3b82f6');
    const memPath = VizHelpers.genSparkline(h.memAvailHist.map(v => 100 - v), '#a855f7');

    const cpuStroke = h.cpu >= 90 ? '#f97316' : (h.cpu >= 75 ? '#fbbf24' : '#3b82f6');
    const memStroke = h.memUsedPct >= 90 ? '#f97316' : (h.memUsedPct >= 75 ? '#fbbf24' : '#a855f7');

    const uptimeStr = VizHelpers.formatUptime(h.uptime);
    const uptimeDays = Math.floor(h.uptime / 86400);

    // Calculate percentiles for CPU history
    const cpuHistSorted = [...h.cpuHist].sort((a, b) => a - b);
    const cpuP50 = cpuHistSorted[Math.floor(cpuHistSorted.length * 0.5)] || 0;
    const cpuP95 = cpuHistSorted[Math.floor(cpuHistSorted.length * 0.95)] || 0;
    const cpuMax = Math.max(...h.cpuHist, 0);

    // Network throughput average
    const netTotalBps = h.netInBps + h.netOutBps;
    const netTotalMbps = (netTotalBps / 1024 / 1024 * 8).toFixed(1);

    return `
      <div class="analytics-card ${statusCls}">
        <!-- Card Header -->
        <div class="card-header">
          <div class="card-title-section">
            <div class="host-icon-large">üñ•Ô∏è</div>
            <div>
              <h2 class="host-title">${h.name}</h2>
              <div class="host-meta">
                <span class="meta-item">‚è± Uptime: <strong>${uptimeStr}</strong> (${uptimeDays}d)</span>
                <span class="meta-item">üì° Status: <strong class="status-${statusCls}">${statusCls.toUpperCase()}</strong></span>
                ${h.triggers > 0 ? `<span class="meta-item alert">üö® <strong>${h.triggers} alerts</strong></span>` : ''}
              </div>
            </div>
          </div>
          <div class="card-summary">
            <div class="summary-metric">
              <div class="summary-label">CPU Usage</div>
              <div class="summary-value ${cpuCls}">${Math.round(h.cpu)}%</div>
            </div>
            <div class="summary-metric">
              <div class="summary-label">RAM Usage</div>
              <div class="summary-value ${memCls}">${Math.round(h.memUsedPct)}%</div>
            </div>
            <div class="summary-metric">
              <div class="summary-label">Disk Usage</div>
              <div class="summary-value ${diskCls}">${Math.round(h.diskUsedPct)}%</div>
            </div>
          </div>
        </div>

        <!-- Metrics Grid -->
        <div class="metrics-grid">
          <!-- CPU Analysis -->
          <div class="metric-section">
            <div class="section-header">
              <span class="section-icon">‚ö°</span>
              <span class="section-title">CPU Analysis</span>
            </div>
            <div class="chart-container">
              <svg viewBox="0 0 200 80" preserveAspectRatio="none" class="trend-chart">
                <defs>
                  <linearGradient id="cpuGrad-${h.name.replace(/\s/g, '')}" x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" style="stop-color:${cpuStroke};stop-opacity:0.3" />
                    <stop offset="100%" style="stop-color:${cpuStroke};stop-opacity:0" />
                  </linearGradient>
                </defs>
                <path d="${cpuPath} L 200 80 L 0 80 Z" fill="url(#cpuGrad-${h.name.replace(/\s/g, '')})" />
                <path d="${cpuPath}" fill="none" stroke="${cpuStroke}" stroke-width="3" vector-effect="non-scaling-stroke"/>
              </svg>
            </div>
            <div class="stats-row">
              <div class="stat-item">
                <div class="stat-label">Current</div>
                <div class="stat-value ${cpuCls}">${Math.round(h.cpu)}%</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">P50</div>
                <div class="stat-value">${Math.round(cpuP50)}%</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">P95</div>
                <div class="stat-value">${Math.round(cpuP95)}%</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">Peak</div>
                <div class="stat-value">${Math.round(cpuMax)}%</div>
              </div>
            </div>
          </div>

          <!-- Memory Analysis -->
          <div class="metric-section">
            <div class="section-header">
              <span class="section-icon">üíæ</span>
              <span class="section-title">Memory Analysis</span>
            </div>
            <div class="chart-container">
              <svg viewBox="0 0 200 80" preserveAspectRatio="none" class="trend-chart">
                <defs>
                  <linearGradient id="memGrad-${h.name.replace(/\s/g, '')}" x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" style="stop-color:${memStroke};stop-opacity:0.3" />
                    <stop offset="100%" style="stop-color:${memStroke};stop-opacity:0" />
                  </linearGradient>
                </defs>
                <path d="${memPath} L 200 80 L 0 80 Z" fill="url(#memGrad-${h.name.replace(/\s/g, '')})" />
                <path d="${memPath}" fill="none" stroke="${memStroke}" stroke-width="3" vector-effect="non-scaling-stroke"/>
              </svg>
            </div>
            <div class="stats-row">
              <div class="stat-item">
                <div class="stat-label">Used</div>
                <div class="stat-value ${memCls}">${VizHelpers.formatBytes(h.memTotal - h.memAvail)}</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">Available</div>
                <div class="stat-value">${VizHelpers.formatBytes(h.memAvail)}</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">Total</div>
                <div class="stat-value">${VizHelpers.formatBytes(h.memTotal)}</div>
              </div>
              <div class="stat-item">
                <div class="stat-label">Usage</div>
                <div class="stat-value ${memCls}">${Math.round(h.memUsedPct)}%</div>
              </div>
            </div>
          </div>

          <!-- Disk & Network -->
          <div class="metric-section">
            <div class="section-header">
              <span class="section-icon">üíø</span>
              <span class="section-title">Storage</span>
            </div>
            <div class="disk-gauge">
              <svg viewBox="0 0 120 120" class="gauge-svg">
                <circle cx="60" cy="60" r="50" fill="none" stroke="rgba(255,255,255,0.1)" stroke-width="12" />
                <circle cx="60" cy="60" r="50" fill="none" stroke="${h.diskUsedPct >= 90 ? '#f97316' : (h.diskUsedPct >= 80 ? '#fbbf24' : '#4ade80')}" stroke-width="12" 
                  stroke-dasharray="${2 * Math.PI * 50}" 
                  stroke-dashoffset="${2 * Math.PI * 50 * (1 - h.diskUsedPct / 100)}" 
                  stroke-linecap="round" 
                  transform="rotate(-90 60 60)" />
                <text x="60" y="55" text-anchor="middle" fill="#e2e8f0" font-size="24" font-weight="bold">${Math.round(h.diskUsedPct)}%</text>
                <text x="60" y="75" text-anchor="middle" fill="#64748b" font-size="10">DISK USED</text>
              </svg>
            </div>
            <div class="section-header" style="margin-top: 20px;">
              <span class="section-icon">üìä</span>
              <span class="section-title">Network</span>
            </div>
            <div class="network-stats">
              <div class="net-stat">
                <div class="net-direction">‚Üì INBOUND</div>
                <div class="net-value">${VizHelpers.formatBytes(h.netInBps)}/s</div>
              </div>
              <div class="net-stat">
                <div class="net-direction">‚Üë OUTBOUND</div>
                <div class="net-value">${VizHelpers.formatBytes(h.netOutBps)}/s</div>
              </div>
              <div class="net-stat total">
                <div class="net-direction">‚ö° TOTAL</div>
                <div class="net-value">${netTotalMbps} Mbps</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    `;
  }).join('');

  // ========== RENDER ==========
  htmlNode.innerHTML = `
    <style>
      @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

      * { margin: 0; padding: 0; box-sizing: border-box; }

      .infra-container {
        font-family: 'Inter', sans-serif;
        background: #0a0a0f;
        min-height: 100vh;
        color: #fff;
        padding: 20px;
      }

      /* ========== HEADER ========== */
      .infra-header {
        background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
        border: 1px solid rgba(255,255,255,0.1);
        border-radius: 12px;
        padding: 20px 30px;
        margin-bottom: 25px;
        display: flex;
        align-items: center;
        justify-content: space-between;
        flex-wrap: wrap;
        gap: 20px;
      }

      .header-left {
        display: flex;
        align-items: center;
        gap: 15px;
      }

      .header-icon {
        width: 50px;
        height: 50px;
        background: linear-gradient(135deg, #3b82f6, #2563eb);
        border-radius: 12px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 26px;
      }

      .header-text .title {
        font-size: 24px;
        font-weight: 700;
        color: #e2e8f0;
      }

      .header-text .title span {
        color: #3b82f6;
      }

      .header-text .subtitle {
        font-size: 11px;
        color: #8892b0;
        text-transform: uppercase;
        letter-spacing: 1px;
        margin-top: 2px;
      }

      .header-stats {
        display: flex;
        gap: 20px;
        flex: 1;
        justify-content: center;
      }

      .stat-box {
        background: rgba(255,255,255,0.05);
        border-radius: 8px;
        padding: 12px 20px;
        min-width: 100px;
        text-align: center;
        position: relative;
      }

      .stat-box .stat-value {
        font-size: 28px;
        font-weight: 700;
      }

      .stat-box .stat-label {
        font-size: 10px;
        color: #8892b0;
        text-transform: uppercase;
        letter-spacing: 1px;
        margin-top: 4px;
      }

      .stat-box.online .stat-value { color: #4ade80; }
      .stat-box.warning .stat-value { color: #fbbf24; }
      .stat-box.critical .stat-value { color: #f97316; }
      .stat-box.offline .stat-value { color: #ef4444; }
      .stat-box.triggers .stat-value { color: #a855f7; }

      .header-right {
        text-align: right;
      }

      .update-time {
        font-size: 14px;
        color: #ccd6f6;
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .pulse-dot {
        width: 8px;
