        0%, 100% { opacity: 1; }
        50% { opacity: 0.5; }
      }

      .update-label {
        font-size: 10px;
        color: #64748b;
        text-transform: uppercase;
        margin-top: 2px;
      }

      /* ========== SUMMARY METRICS ========== */
