      }

      .summary-card .card-value {
        font-size: 36px;
        font-weight: 700;
        color: #e2e8f0;
      }

      .summary-card .card-label {
        font-size: 11px;
        color: #64748b;
        margin-top: 4px;
      }

      /* ========== HOST CARDS GRID ========== */
      .section-header {
        display: flex;
        align-items: center;
        gap: 10px;
        margin-bottom: 20px;
        padding-bottom: 12px;
        border-bottom: 1px solid rgba(255,255,255,0.08);
      }

